        [836] = {2018601,2,2,"袁术手下",123,0,"孙氏世代在吴地作官，家在富春。听闻这孙坚出生前，孙氏祖坟数有光怪，五色云气，上连于天，蔓延数里远近。","孙氏世代在吴地作官，家在富春。听闻这孙坚出生前，孙氏祖坟数有光怪，五色云气，上连于天，蔓延数里远近。",1,"story_yssx_1","story_yssx_1","袁术",412,},
        [837] = {2018601,3,2,"袁术手下",123,0,"孙氏世代在吴地作官，家在富春。听闻这孙坚出生前，孙氏祖坟数有光怪，五色云气，上连于天，蔓延数里远近。","孙氏世代在吴地作官，家在富春。听闻这孙坚出生前，孙氏祖坟数有光怪，五色云气，上连于天，蔓延数里远近。",1,"story_yssx_2","story_yssx_2","袁术",412,},
