        [839] = {2018601,5,2,"袁术手下",123,0,"当地父老们都说：“这不是一般景象，孙家恐怕要强盛起来了。”等到孙坚出生，果然容貌不凡。","当地父老们都说：“这不是一般景象，孙家恐怕要强盛起来了。”等到孙坚出生，果然容貌不凡。",1,"story_yssx_3","story_yssx_3","袁术",412,},
        [840] = {2018601,6,2,"袁术手下",123,0,"当地父老们都说：“这不是一般景象，孙家恐怕要强盛起来了。”等到孙坚出生，果然容貌不凡。","当地父老们都说：“这不是一般景象，孙家恐怕要强盛起来了。”等到孙坚出生，果然容貌不凡。",1,"story_yssx_4","story_yssx_4","袁术",412,},
        [841] = {2018601,7,2,"袁术手下",123,0,"孙坚年少时曾历任三县县丞，甚有声望，后来绞杀叛军有功，便升了长沙太守。","孙坚年少时曾历任三县县丞，甚有声望，后来绞杀叛军有功，便升了长沙太守。",1,"story_yssx_5","story_yssx_5","袁术",412,},
        [842] = {2018601,8,1,"袁术",412,0,"他再怎么能耐，怎比得过我袁家四世三公。","他再怎么能耐，怎比得过我袁家四世三公。",1,"story_ys2_8","story_ys2_8","袁术手下",123,},
