        [844] = {2018601,10,1,"袁术",412,0,"也好，那就表奏他为破虏将军，兼领豫州刺史。","也好，那就表奏他为破虏将军，兼领豫州刺史。",1,"story_ys2_9","story_ys2_9","袁术手下",123,},
        [845] = {2018602,1,2,"刘表",423,0,"孙文台，何故经过我荆州？","孙文台，何故经过我荆州？",1,"story_lb3_5","story_lb3_5","孙坚",309,},
        [846] = {2018602,2,1,"孙坚",309,0,"十八路诸侯尔虞我诈勾心斗角，孙某决定返回江东。","十八路诸侯尔虞我诈勾心斗角，孙某决定返回江东。",1,"story_sj2_8","story_sj2_8","刘表",423,},
        [847] = {2018602,3,1,"孙坚",309,0,"当初我与袁绍袁术同举义兵，目的是挽救江山社稷。如今逆贼将被扫灭，内部却如此争斗，我跟谁戮力同心，回天转日呢？","当初我与袁绍袁术同举义兵，目的是挽救江山社稷。如今逆贼将被扫灭，内部却如此争斗，我跟谁戮力同心，回天转日呢？",1,"story_sj2_9","story_sj2_9","刘表",423,},
        [848] = {2018602,4,1,"孙坚",309,0,"当初我与袁绍袁术同举义兵，目的是挽救江山社稷。如今逆贼将被扫灭，内部却如此争斗，我跟谁戮力同心，回天转日呢？","当初我与袁绍袁术同举义兵，目的是挽救江山社稷。如今逆贼将被扫灭，内部却如此争斗，我跟谁戮力同心，回天转日呢？",1,"story_sj2_10","story_sj2_10","刘表",423,},
        [849] = {2018602,5,2,"刘表",423,0,"你要回江东便回江东，可玉玺乃朝廷重宝，你却匿之而去，意欲何为？","你要回江东便回江东，可玉玺乃朝廷重宝，你却匿之而去，意欲何为？",1,"story_lb3_6","story_lb3_6","孙坚",309,},
        [850] = {2018602,6,1,"孙坚",309,0,"（大怒）要战便战，干你何事？","（大怒）要战便战，干你何事？",1,"story_sj2_11","story_sj2_11","刘表",423,},
        [851] = {2018603,1,2,"韩当",318,0,"将军，今日狂风骤起，将中军帅字旗竿吹折，此非吉兆，可暂班师。","将军，今日狂风骤起，将中军帅字旗竿吹折，此非吉兆，可暂班师。",1,"story_hd_1","story_hd_1","孙坚",309,},
        [852] = {2018603,2,1,"孙坚",309,0,"吾屡战屡胜，取襄阳只在旦夕；岂可因风折旗竿，遽尔罢兵！","吾屡战屡胜，取襄阳只在旦夕；岂可因风折旗竿，遽尔罢兵！",1,"story_sj2_12","story_sj2_12","韩当",318,},
        [853] = {2018603,3,2,"蒯良",140,0,"某夜观天象，见一将星欲坠。以分野度之，当应在孙坚。主公可速致书袁绍，求其相助。","某夜观天象，见一将星欲坠。以分野度之，当应在孙坚。主公可速致书袁绍，求其相助。",1,"story_kn_1","story_kn_1","孙坚",309,},
