        [857] = {2018603,7,2,"蒯良",140,0,"汝既敢去，可听吾计。给你五百军马，多带能射者冲出阵去，即奔岘山。","汝既敢去，可听吾计。给你五百军马，多带能射者冲出阵去，即奔岘山。",1,"story_kn_3","story_kn_3","吕公",129,},
