        [860] = {2018603,10,2,"蒯良",140,0,"孙坚必引军来赶，汝分一百人上山，寻石子准备；一百人执弓弩伏于林中。将敌人引到埋伏之处，矢石俱发。","孙坚必引军来赶，汝分一百人上山，寻石子准备；一百人执弓弩伏于林中。将敌人引到埋伏之处，矢石俱发。",1,"story_kn_6","story_kn_6","吕公",129,},
