        [900] = {2018803,1,1,"大乔",302,0,"宛城已破，父亲大人不得已将我许配给敌方大将孙策……","宛城已破，父亲大人不得已将我许配给敌方大将孙策……",1,"story_dq_7","story_dq_7","",0,},
        [901] = {2018803,2,1,"大乔",302,0,"哎，真不知那孙策是何许人也，我竟连一面都没见过，就要嫁给此人。","哎，真不知那孙策是何许人也，我竟连一面都没见过，就要嫁给此人。",1,"story_dq_8","story_dq_8","",0,},
        [902] = {2018803,3,2,"孙策",301,0,"姑娘，可还记得我？自那日一见后，我便对你念念不忘，你可愿意嫁给我，永结秦晋之好？","姑娘，可还记得我？自那日一见后，我便对你念念不忘，你可愿意嫁给我，永结秦晋之好？",1,"story_sc_12","story_sc_12","大乔",302,},
