        [905] = {2018803,6,2,"孙策",301,0,"姑娘可是害羞，不敢言语。无妨，你若愿意，变点点头，你若不愿意，便摇摇头。","姑娘可是害羞，不敢言语。无妨，你若愿意，变点点头，你若不愿意，便摇摇头。",1,"story_sc_14","story_sc_14","大乔",302,},
        [906] = {2018803,7,1,"大乔",302,0,"（轻轻的点点头，用很轻的声音道）公子用真心待我，大乔愿还以真心，永不变心！","（轻轻的点点头，用很轻的声音道）公子用真心待我，大乔愿还以真心，永不变心！",1,"story_dq_10","story_dq_10","孙策",301,},
        [907] = {2018803,8,1,"大乔",302,0,"（轻轻的点点头，用很轻的声音道）公子用真心待我，大乔愿还以真心，永不变心！","（轻轻的点点头，用很轻的声音道）公子用真心待我，大乔愿还以真心，永不变心！",1,"story_dq_11","story_dq_11","孙策",301,},
        [908] = {2018901,1,2,"涿郡太守",138,0,"我的乖女儿，我今日为你定了一门好亲事！","我的乖女儿，我今日为你定了一门好亲事！",1,"story_zjts_1","story_zjts_1","公孙夫人",130,},
        [909] = {2018901,2,1,"公孙夫人",130,0,"父亲，女儿的终身大事，你怎可如此莽撞！","父亲，女儿的终身大事，你怎可如此莽撞！",1,"story_gsfr_1","story_gsfr_1","涿郡太守",138,},
        [910] = {2018901,3,2,"涿郡太守",138,0,"为父为你定的这门亲事，你肯定满意。此人名叫公孙瓒，出身辽西贵族公孙氏。","为父为你定的这门亲事，你肯定满意。此人名叫公孙瓒，出身辽西贵族公孙氏。",1,"story_zjts_2","story_zjts_2","公孙夫人",130,},
        [911] = {2018901,4,2,"涿郡太守",138,0,"这公孙瓒相貌俊美，声音洪亮，机智善辩，将来前途不可限量。","这公孙瓒相貌俊美，声音洪亮，机智善辩，将来前途不可限量。",1,"story_zjts_3","story_zjts_3","公孙夫人",130,},
