        [913] = {2018901,6,2,"涿郡太守",138,0,"好好好，我女儿以后就是公孙夫人了，哈哈！","好好好，我女儿以后就是公孙夫人了，哈哈！",1,"story_zjts_4","story_zjts_4","公孙夫人",130,},
        [914] = {2018902,1,2,"刘备",202,0,"公孙瓒，你这家伙，运气真好！","公孙瓒，你这家伙，运气真好！",1,"story_lb2_16","story_lb2_16","公孙瓒",418,},
        [915] = {2018902,2,1,"公孙瓒",418,0,"刘备，你可是中山靖王之后，我哪里能跟你比！","刘备，你可是中山靖王之后，我哪里能跟你比！",1,"story_gsz_10","story_gsz_10","刘备",202,},
        [916] = {2018902,3,2,"刘备",202,0,"这得了便宜还卖乖哈！你刚做了涿郡太守的女婿，你岳父又给你弄了个文书，让你去卢植为师，真是前途不可限量啊。","这得了便宜还卖乖哈！你刚做了涿郡太守的女婿，你岳父又给你弄了个文书，让你去卢植为师，真是前途不可限量啊。",1,"story_lb2_17","story_lb2_17","公孙瓒",418,},
        [917] = {2018902,4,2,"刘备",202,0,"这得了便宜还卖乖哈！你刚做了涿郡太守的女婿，你岳父又给你弄了个文书，让你去卢植为师，真是前途不可限量啊。","这得了便宜还卖乖哈！你刚做了涿郡太守的女婿，你岳父又给你弄了个文书，让你去卢植为师，真是前途不可限量啊。",1,"story_lb2_18","story_lb2_18","公孙瓒",418,},
        [918] = {2018902,5,1,"公孙瓒",418,0,"哈哈，你不是也要拜卢植为师吗！好说好说，你我同窗又是同乡，以后彼此照应就是。","哈哈，你不是也要拜卢植为师吗！好说好说，你我同窗又是同乡，以后彼此照应就是。",1,"story_gsz_11","story_gsz_11","刘备",202,},
