        [1471] = {20184003,3,2,"关羽",205,0,"解这白马之围，我一人便可，等我策马斩其颜良首级。","解这白马之围，我一人便可，等我策马斩其颜良首级。",1,"story_gy1_19","story_gy1_19","荀攸",120,},
        [1472] = {20184004,1,1,"文丑",414,0,"白马之战，就是你斩了我兄弟颜良？","白马之战，就是你斩了我兄弟颜良？",1,"story_wc1_4","story_wc1_4","关羽",205,},
