        [1574] = {20184305,7,1,"李傕",431,0,"哈哈！老弱者杀之，强壮者充军，临敌则驱民兵在前，名曰“敢死军”，再令军士将衣服物件抛弃于道，扰乱敌人军心，何愁敌人不乱，你我不胜呢？","哈哈！老弱者杀之，强壮者充军，临敌则驱民兵在前，名曰“敢死军”，再令军士将衣服物件抛弃于道，扰乱敌人军心，何愁敌人不乱，你我不胜呢？",1,"story_lj_23","story_lj_23","郭汜",142,},
