        [1769] = {20185503,2,1,"曹操",103,0,"沮授你可降？","沮授你可降？",1,"story_cc1_174","story_cc1_174","沮授",422,},
        [1770] = {20185503,3,2,"沮授",422,0,"授不降也！","授不降也！",1,"story_js1_13","story_js1_13","曹操",103,},
        [1771] = {20185503,4,1,"曹操",103,0,"本初无谋，不用君言，君何尚执迷耶？吾若早得足下，天下不足虑也。","本初无谋，不用君言，君何尚执迷耶？吾若早得足下，天下不足虑也。",1,"story_cc1_175","story_cc1_175","沮授",422,},
        [1772] = {20185503,5,1,"曹操",103,0,"本初无谋，不用君言，君何尚执迷耶？吾若早得足下，天下不足虑也。","本初无谋，不用君言，君何尚执迷耶？吾若早得足下，天下不足虑也。",1,"story_cc1_176","story_cc1_176","沮授",422,},
        [1773] = {20185601,1,1,"曹仁",106,0,"今汉室无主，董卓专权，欺君害民，天下切齿。","今汉室无主，董卓专权，欺君害民，天下切齿。",1,"story_cr_5","story_cr_5","曹洪",142,},
        [1774] = {20185601,2,1,"曹仁",106,0,"曹操竖招兵白旗一面，上书“忠义”二字，招集义兵。","曹操竖招兵白旗一面，上书“忠义”二字，招集义兵。",1,"story_cr_6","story_cr_6","曹洪",142,},
        [1775] = {20185601,3,1,"曹仁",106,0,"子廉，曹操乃我们从兄，我们去投奔他吧。","子廉，曹操乃我们从兄，我们去投奔他吧。",1,"story_cr_7","story_cr_7","曹洪",142,},
        [1776] = {20185601,4,2,"曹洪",142,0,"子孝，我也正有此打算。","子孝，我也正有此打算。",1,"story_ch1_1","story_ch1_1","曹仁",106,},
        [1777] = {20185602,1,1,"曹仁",106,0,"前不听公言，兵败折二将，失却襄阳，如之奈何？","前不听公言，兵败折二将，失却襄阳，如之奈何？",1,"story_cr_8","story_cr_8","满宠",135,},
        [1778] = {20185602,2,2,"满宠",135,0,"云长虎将，足智多谋，不可轻敌，只宜坚守。","云长虎将，足智多谋，不可轻敌，只宜坚守。",1,"story_mc2_1","story_mc2_1","曹仁",106,},
        [1779] = {20185602,3,1,"曹仁",106,0,"速差人求救，使命星夜至长安，将书上言：云长破了襄阳，现围樊城甚急。望拨大将前来救援。","速差人求救，使命星夜至长安，将书上言：云长破了襄阳，现围樊城甚急。望拨大将前来救援。",1,"story_cr_9","story_cr_9","满宠",135,},
        [1780] = {20185602,4,1,"曹仁",106,0,"誓死守城！","誓死守城！",1,"story_cr_10","story_cr_10","满宠",135,},
        [1781] = {20185603,1,1,"曹仁",106,0,"樊城周围，白浪滔天，水势益甚，城垣渐渐浸塌，男女担土搬砖，填塞不住。","樊城周围，白浪滔天，水势益甚，城垣渐渐浸塌，男女担土搬砖，填塞不住。",1,"story_cr_11","story_cr_11","满宠",135,},
        [1782] = {20185603,2,1,"曹仁",106,0,"今日之危，非力可救，何不趁敌军未至，乘舟夜走，虽然失城，尚可全身。","今日之危，非力可救，何不趁敌军未至，乘舟夜走，虽然失城，尚可全身。",1,"story_cr_12","story_cr_12","满宠",135,},
        [1783] = {20185603,3,2,"满宠",135,0,"不可。山水骤至，岂能长存？不旬日即当自退。关公虽未攻城，已遣别将在郏下。","不可。山水骤至，岂能长存？不旬日即当自退。关公虽未攻城，已遣别将在郏下。",1,"story_mc2_2","story_mc2_2","曹仁",106,},
        [1784] = {20185603,4,2,"满宠",135,0,"不可。山水骤至，岂能长存？不旬日即当自退。关公虽未攻城，已遣别将在郏下。","不可。山水骤至，岂能长存？不旬日即当自退。关公虽未攻城，已遣别将在郏下。",1,"story_mc2_3","story_mc2_3","曹仁",106,},
        [1785] = {20185603,5,2,"满宠",135,0,"其所以不敢轻进者，虑吾军袭其后也。今若弃城而去，黄河以南，非国家之有矣。","其所以不敢轻进者，虑吾军袭其后也。今若弃城而去，黄河以南，非国家之有矣。",1,"story_mc2_4","story_mc2_4","曹仁",106,},
        [1786] = {20185603,6,1,"曹仁",106,0,"非伯宁之教，几误大事。","非伯宁之教，几误大事。",1,"story_cr_13","story_cr_13","满宠",135,},
        [1787] = {20185603,7,1,"曹仁",106,0,"吾受魏王命，保守此城，但有言弃城而去者斩！","吾受魏王命，保守此城，但有言弃城而去者斩！",1,"story_cr_14","story_cr_14","满宠",135,},
        [1788] = {20185603,8,1,"曹仁",106,0,"各将士死守樊城，城上设弓弩数百，军士昼夜防护，不敢懈怠。","各将士死守樊城，城上设弓弩数百，军士昼夜防护，不敢懈怠。",1,"story_cr_15","story_cr_15","满宠",135,},
        [1789] = {20185604,1,1,"曹仁",106,0,"关羽被我弓弩所伤，被徐晃击败，被迫退守麦城。","关羽被我弓弩所伤，被徐晃击败，被迫退守麦城。",1,"story_cr_16","story_cr_16","",0,},
        [1790] = {20185604,2,1,"曹仁",106,0,"可惜可惜，我未能亲征麦城，在阵前生擒关羽！","可惜可惜，我未能亲征麦城，在阵前生擒关羽！",1,"story_cr_17","story_cr_17","",0,},
        [1791] = {20185604,3,1,"曹仁",106,0,"反倒被那东吴的吕蒙小儿捡了便宜，真是遗憾。","反倒被那东吴的吕蒙小儿捡了便宜，真是遗憾。",1,"story_cr_18","story_cr_18","",0,},
        [1792] = {20185701,1,1,"吴国太",322,0,"男大须婚，女大须嫁，古今常理。我为你母亲，事当禀命于我。","男大须婚，女大须嫁，古今常理。我为你母亲，事当禀命于我。",1,"story_wgt_1","story_wgt_1","孙权",306,},
        [1793] = {20185701,2,1,"吴国太",322,0,"你招刘玄德为婿，如何瞒我？女儿须是我的！","你招刘玄德为婿，如何瞒我？女儿须是我的！",1,"story_wgt_2","story_wgt_2","孙权",306,},
        [1794] = {20185701,3,2,"孙权",306,0,"那里得这话来？","那里得这话来？",1,"story_sq1_27","story_sq1_27","吴国太",322,},
        [1795] = {20185701,4,1,"吴国太",322,0,"若要不知，除非莫为。满城百姓，那一个不知？你倒瞒我！","若要不知，除非莫为。满城百姓，那一个不知？你倒瞒我！",1,"story_wgt_3","story_wgt_3","孙权",306,},
        [1796] = {20185701,5,1,"吴国太",322,0,"若要不知，除非莫为。满城百姓，那一个不知？你倒瞒我！","若要不知，除非莫为。满城百姓，那一个不知？你倒瞒我！",1,"story_wgt_4","story_wgt_4","孙权",306,},
        [1797] = {20185701,6,2,"孙权",306,0,"非也。此是周瑜之计，因要取荆州，故将此为名，赚刘备来拘囚在此，要他把荆州来换。","非也。此是周瑜之计，因要取荆州，故将此为名，赚刘备来拘囚在此，要他把荆州来换。",1,"story_sq1_28","story_sq1_28","吴国太",322,},
        [1798] = {20185701,7,2,"孙权",306,0,"非也。此是周瑜之计，因要取荆州，故将此为名，赚刘备来拘囚在此，要他把荆州来换。","非也。此是周瑜之计，因要取荆州，故将此为名，赚刘备来拘囚在此，要他把荆州来换。",1,"story_sq1_29","story_sq1_29","吴国太",322,},
        [1799] = {20185701,8,2,"孙权",306,0,"若其不从，先斩刘备。此是计策，非实意也。","若其不从，先斩刘备。此是计策，非实意也。",1,"story_sq1_30","story_sq1_30","吴国太",322,},
        [1800] = {20185701,9,1,"吴国太",322,0,"汝做六郡八十一州大都督，直恁无条计策去取荆州，却将我女儿为名，使美人计！","汝做六郡八十一州大都督，直恁无条计策去取荆州，却将我女儿为名，使美人计！",1,"story_wgt_5","story_wgt_5","孙权",306,},
        [1801] = {20185701,10,1,"吴国太",322,0,"杀了刘备，我女便是望门寡，明日再怎的说亲？须误了我女儿一世！你们好做作！","杀了刘备，我女便是望门寡，明日再怎的说亲？须误了我女儿一世！你们好做作！",1,"story_wgt_6","story_wgt_6","孙权",306,},
        [1802] = {20185701,11,1,"吴国太",322,0,"若用此计，便得荆州，也被天下人耻笑。此事如何行得！","若用此计，便得荆州，也被天下人耻笑。此事如何行得！",1,"story_wgt_7","story_wgt_7","孙权",306,},
        [1803] = {20185701,12,1,"吴国太",322,0,"明日约在甘露寺相见：如不中我意，任从你们行事；若中我的意，我自把女儿嫁他！","明日约在甘露寺相见：如不中我意，任从你们行事；若中我的意，我自把女儿嫁他！",1,"story_wgt_8","story_wgt_8","孙权",306,},
        [1804] = {20185701,13,1,"吴国太",322,0,"明日约在甘露寺相见：如不中我意，任从你们行事；若中我的意，我自把女儿嫁他！","明日约在甘露寺相见：如不中我意，任从你们行事；若中我的意，我自把女儿嫁他！",1,"story_wgt_9","story_wgt_9","孙权",306,},
        [1805] = {20185702,1,1,"孙权",306,0,"我母亲力主，已将吾妹嫁刘备。不想弄假成真。此事还复如何？","我母亲力主，已将吾妹嫁刘备。不想弄假成真。此事还复如何？",1,"story_sq1_31","story_sq1_31","周瑜",303,},
        [1806] = {20185702,2,2,"周瑜",303,0,"瑜所谋之事，不想反覆如此。既已弄假成真，又当就此用计。","瑜所谋之事，不想反覆如此。既已弄假成真，又当就此用计。",1,"story_zy2_16","story_zy2_16","孙权",306,},
        [1807] = {20185702,3,2,"周瑜",303,0,"瑜所谋之事，不想反覆如此。既已弄假成真，又当就此用计。","瑜所谋之事，不想反覆如此。既已弄假成真，又当就此用计。",1,"story_zy2_17","story_zy2_17","孙权",306,},
        [1808] = {20185702,4,2,"周瑜",303,0,"刘备以枭雄之姿，有关、张、赵云之将，更兼诸葛用谋，必非久屈人下者。","刘备以枭雄之姿，有关、张、赵云之将，更兼诸葛用谋，必非久屈人下者。",1,"story_zy2_18","story_zy2_18","孙权",306,},
        [1809] = {20185702,5,2,"周瑜",303,0,"愚意莫如软困之于吴中，盛为筑宫室，以丧其心志，多送美色玩好，以娱其耳目。","愚意莫如软困之于吴中，盛为筑宫室，以丧其心志，多送美色玩好，以娱其耳目。",1,"story_zy2_19","story_zy2_19","孙权",306,},
        [1810] = {20185702,6,2,"周瑜",303,0,"使分开关、张之情，隔远诸葛之契，各置一方，然后以兵击之，大事可定矣。","使分开关、张之情，隔远诸葛之契，各置一方，然后以兵击之，大事可定矣。",1,"story_zy2_20","story_zy2_20","孙权",306,},
        [1811] = {20185702,7,2,"周瑜",303,0,"今若纵之，恐蛟龙得云雨，终非池中物也。愿明公熟思之。","今若纵之，恐蛟龙得云雨，终非池中物也。愿明公熟思之。",1,"story_zy2_21","story_zy2_21","孙权",306,},
        [1812] = {20185702,8,1,"孙权",306,0,"公瑾之谋，正合愚意。刘备起身微末，奔走天下，未尝受享富贵。","公瑾之谋，正合愚意。刘备起身微末，奔走天下，未尝受享富贵。",1,"story_sq1_32","story_sq1_32","周瑜",303,},
        [1813] = {20185702,9,1,"孙权",306,0,"今若以华堂大厦，子女金帛，令彼享用，自然疏远孔明、关、张等。","今若以华堂大厦，子女金帛，令彼享用，自然疏远孔明、关、张等。",1,"story_sq1_33","story_sq1_33","周瑜",303,},
        [1814] = {20185702,10,1,"孙权",306,0,"使彼各生怨望，然后荆州可图也。","使彼各生怨望，然后荆州可图也。",1,"story_sq1_34","story_sq1_34","周瑜",303,},
        [1815] = {20185703,1,2,"孙尚香",310,0,"丈夫何故烦恼？","丈夫何故烦恼？",1,"story_ssx_4","story_ssx_4","刘备",202,},
        [1816] = {20185703,2,1,"刘备",202,0,"念备一身飘荡异乡，生不能侍奉二亲，又不能祭祀宗祖，乃大逆不孝也。","念备一身飘荡异乡，生不能侍奉二亲，又不能祭祀宗祖，乃大逆不孝也。",1,"story_lb2_39","story_lb2_39","孙尚香",310,},
        [1817] = {20185703,3,1,"刘备",202,0,"今岁旦在迩，使备悒怏不已。","今岁旦在迩，使备悒怏不已。",1,"story_lb2_40","story_lb2_40","孙尚香",310,},
        [1818] = {20185703,4,2,"孙尚香",310,0,"你休瞒我，我已听知了也！方才赵子龙报说荆州危急，你欲还乡，故推此意。","你休瞒我，我已听知了也！方才赵子龙报说荆州危急，你欲还乡，故推此意。",1,"story_ssx_5","story_ssx_5","刘备",202,},
        [1819] = {20185703,5,1,"刘备",202,0,"夫人既知，备安敢相瞒。备欲不去，使荆州有失，被天下人耻笑。","夫人既知，备安敢相瞒。备欲不去，使荆州有失，被天下人耻笑。",1,"story_lb2_41","story_lb2_41","孙尚香",310,},
        [1820] = {20185703,6,1,"刘备",202,0,"欲去，又舍不得夫人，因此烦恼。","欲去，又舍不得夫人，因此烦恼。",1,"story_lb2_42","story_lb2_42","孙尚香",310,},
        [1821] = {20185703,7,2,"孙尚香",310,0,"妾已事君，任君所之，妾当相随。","妾已事君，任君所之，妾当相随。",1,"story_ssx_6","story_ssx_6","刘备",202,},
        [1822] = {20185703,8,1,"刘备",202,0,"夫人之心，虽则如此，争奈国太与吴侯安肯容夫人去？夫人若可怜刘备，暂时辞别。","夫人之心，虽则如此，争奈国太与吴侯安肯容夫人去？夫人若可怜刘备，暂时辞别。",1,"story_lb2_43","story_lb2_43","孙尚香",310,},
        [1823] = {20185703,9,2,"孙尚香",310,0,"丈夫休得烦恼。妾当苦告母亲，必放妾与君同去。","丈夫休得烦恼。妾当苦告母亲，必放妾与君同去。",1,"story_ssx_7","story_ssx_7","刘备",202,},
        [1824] = {20185703,10,1,"刘备",202,0,"纵然国太肯时，吴侯必然阻挡。","纵然国太肯时，吴侯必然阻挡。",1,"story_lb2_44","story_lb2_44","孙尚香",310,},
        [1825] = {20185703,11,2,"孙尚香",310,0,"妾与君正旦拜贺时，推称江边祭祖，不告而去，若何？","妾与君正旦拜贺时，推称江边祭祖，不告而去，若何？",1,"story_ssx_8","story_ssx_8","刘备",202,},
        [1826] = {20185703,12,1,"刘备",202,0,"若如此，生死难忘！切勿漏泄。","若如此，生死难忘！切勿漏泄。",1,"story_lb2_45","story_lb2_45","孙尚香",310,},
        [1827] = {20185704,1,2,"赵云",201,0,"嫂夫人为何要如此着急回东吴？","嫂夫人为何要如此着急回东吴？",1,"story_zy1_5","story_zy1_5","孙尚香",310,},
        [1828] = {20185704,2,1,"孙尚香",310,0,"家中来信，母亲病重，我要回去探望。","家中来信，母亲病重，我要回去探望。",1,"story_ssx_9","story_ssx_9","赵云",201,},
        [1829] = {20185704,3,2,"赵云",201,0,"既回家探母，为何要带上阿斗？","既回家探母，为何要带上阿斗？",1,"story_zy1_6","story_zy1_6","孙尚香",310,},
        [1830] = {20185704,4,2,"张飞",206,0,"请嫂夫人不要难为尔等，留下阿斗，汝回东吴探望便是。","请嫂夫人不要难为尔等，留下阿斗，汝回东吴探望便是。",1,"story_zf1_20","story_zf1_20","孙尚香",310,},
