        [2316] = {1702,2,2,"田楷",143,0,"徐州与青州唇亡齿寒，按理我青州应当相助，但眼下青州正与冀州的袁绍开战，若贸然分散兵力，恐怕不妥……","徐州与青州唇亡齿寒，按理我青州应当相助，但眼下青州正与冀州的袁绍开战，若贸然分散兵力，恐怕不妥……",1,"story_tk_1","story_tk_1","",999,},
        [2317] = {1702,3,2,"田楷",143,0,"徐州与青州唇亡齿寒，按理我青州应当相助，但眼下青州正与冀州的袁绍开战，若贸然分散兵力，恐怕不妥……","徐州与青州唇亡齿寒，按理我青州应当相助，但眼下青州正与冀州的袁绍开战，若贸然分散兵力，恐怕不妥……",1,"story_tk_2","story_tk_2","",999,},
