        [2322] = {1703,1,1,"主角",1,0,"大哥，曹操要攻打徐州，我们本来帮陶谦去北海请孔融帮忙出兵，谁料北海被黄巾包围了，那孔融便派我给你送信，让你带兵去援助徐州。","大哥，曹操要攻打徐州，我们本来帮陶谦去北海请孔融帮忙出兵，谁料北海被黄巾包围了，那孔融便派我给你送信，让你带兵去援助徐州。",1,"story_zj2_149","story_nzj2_149","刘备",202,},
