        [2339] = {1801,1,1,"曹操",103,0,"吕布，我曹操与汝素来无仇，为何趁我出兵徐州之际，夺我兖州、濮阳两城？","吕布，我曹操与汝素来无仇，为何趁我出兵徐州之际，夺我兖州、濮阳两城？",1,"story_cc1_219","story_cc1_219","吕布",403,},
