        [2346] = {1802,4,2,"张星彩",216,0,"李傕郭汜乱长安之后，吕布本来去投了袁术，谁知袁术心眼狭小，吕布素有野心，也不肯久居人下。","李傕郭汜乱长安之后，吕布本来去投了袁术，谁知袁术心眼狭小，吕布素有野心，也不肯久居人下。",1,"story_zxc_164","story_zxc_164","",999,},
