        [2355] = {1803,6,2,"张星彩",216,0,"张邈年少时以侠义闻名，接济贫困，助人为乐，不惜倾家荡产，很多名士都归附于他，曹操、袁绍都曾是张邈的朋友。","张邈年少时以侠义闻名，接济贫困，助人为乐，不惜倾家荡产，很多名士都归附于他，曹操、袁绍都曾是张邈的朋友。",1,"story_zxc_170","story_zxc_170","",999,},
        [2356] = {1803,7,2,"张星彩",216,0,"张邈名气越来越大，后来朝廷征召他做官，让他做了陈留太守。","张邈名气越来越大，后来朝廷征召他做官，让他做了陈留太守。",1,"story_zxc_171","story_zxc_171","",999,},
        [2357] = {1803,8,2,"张邈",138,0,"哼！别看老子拿把菜刀，就以为老子是厨子！老子拿的可是绝世宝刀！","哼！别看老子拿把菜刀，就以为老子是厨子！老子拿的可是绝世宝刀！",1,"story_zm_6","story_zm_6","",999,},
        [2358] = {1803,9,1,"主角",1,0,"张邈，看来你实力不错。不过在我面前，那都是渣！","张邈，看来你实力不错。不过在我面前，那都是渣！",1,"story_zj2_159","story_nzj2_159","",999,},
        [2359] = {1804,1,2,"张邈",138,0,"将军果然了得，竟然胜了我！我愿意将这把宝刀送给你！","将军果然了得，竟然胜了我！我愿意将这把宝刀送给你！",1,"story_zm_7","story_zm_7","主角",1,},
        [2360] = {1804,2,1,"主角",1,0,"那还是算了，你这把宝刀，被我打得都卷边了，你还是自己留着切葱吧！","那还是算了，你这把宝刀，被我打得都卷边了，你还是自己留着切葱吧！",1,"story_zj2_160","story_nzj2_160","",999,},
        [2361] = {1804,3,2,"张邈",138,0,"没想到我八厨张邈，也有被人嫌弃的一天！","没想到我八厨张邈，也有被人嫌弃的一天！",1,"story_zm_8","story_zm_8","",999,},
        [2362] = {1805,1,2,"于禁",119,0,"主公且在城外，容我们先入城！","主公且在城外，容我们先入城！",1,"story_yj3_3","story_yj3_3","曹操",103,},
        [2363] = {1805,2,1,"曹操",103,0,"我不自往，谁敢向前！","我不自往，谁敢向前！",1,"story_cc1_221","story_cc1_221","于禁",119,},
