        [2365] = {1805,4,2,"张星彩",216,0,"主公，这曹操误信了陈宫和吕布的奸计，已经领兵冲进城去了！","主公，这曹操误信了陈宫和吕布的奸计，已经领兵冲进城去了！",1,"story_zxc_172","story_zxc_172","",999,},
        [2366] = {1805,5,2,"张星彩",216,0,"里面火光冲天四处浓烟，不知道曹操怎么样了！刚才遇到乐进李典、典韦等人正四处追寻曹操！","里面火光冲天四处浓烟，不知道曹操怎么样了！刚才遇到乐进李典、典韦等人正四处追寻曹操！",1,"story_zxc_173","story_zxc_173","",999,},
