        [2368] = {1806,1,2,"吕布",403,0,"哈哈，这一战打得痛快！小英雄，你我也不是外人，若不是你，哪有我和貂蝉的今天！罢了，就看在你的面上，今日就放过这曹操！","哈哈，这一战打得痛快！小英雄，你我也不是外人，若不是你，哪有我和貂蝉的今天！罢了，就看在你的面上，今日就放过这曹操！",1,"story_lb1_59","story_lb1_59","主角",1,},
        [2369] = {1806,2,2,"吕布",403,0,"哈哈，这一战打得痛快！小英雄，你我也不是外人，若不是你，哪有我和貂蝉的今天！罢了，就看在你的面上，今日就放过这曹操！","哈哈，这一战打得痛快！小英雄，你我也不是外人，若不是你，哪有我和貂蝉的今天！罢了，就看在你的面上，今日就放过这曹操！",1,"story_lb1_60","story_lb1_60","主角",1,},
        [2370] = {1806,3,1,"主角",1,0,"好说，吕温侯果然爽快！","好说，吕温侯果然爽快！",1,"story_zj2_163","story_nzj2_163","吕布",403,},
        [2371] = {1806,4,2,"曹操",103,0,"刚才城门上崩下一块火梁，正砸中我手臂，手臂须发，尽被烧伤！","刚才城门上崩下一块火梁，正砸中我手臂，手臂须发，尽被烧伤！",1,"story_cc1_222","story_cc1_222","主角",1,},
        [2372] = {1806,5,2,"曹操",103,0,"若不是小英雄及时赶到，打败吕布，今日当真危险！这块名将令就送给小英雄以表谢意！","若不是小英雄及时赶到，打败吕布，今日当真危险！这块名将令就送给小英雄以表谢意！",1,"story_cc1_223","story_cc1_223","",999,},
