        [2374] = {1806,7,2,"曹操",103,0,"小英雄尽管放心，我早已派人从徐州撤兵，上次避而不见，还请小英雄见谅。","小英雄尽管放心，我早已派人从徐州撤兵，上次避而不见，还请小英雄见谅。",1,"story_cc1_224","story_cc1_224","",999,},
        [2375] = {1806,8,1,"主角",1,0,"好，既然曹兄信守诺言，那我也就不计较那么多了。曹兄，告辞，我要回徐州找大哥。","好，既然曹兄信守诺言，那我也就不计较那么多了。曹兄，告辞，我要回徐州找大哥。",1,"story_zj2_165","story_nzj2_165","",999,},
