        [2379] = {1901,3,2,"陶谦",434,0,"玄德，你仪表轩昂，语言豁达，德广才高，又是汉室宗亲之后，我情愿将徐州相让！","玄德，你仪表轩昂，语言豁达，德广才高，又是汉室宗亲之后，我情愿将徐州相让！",1,"story_tq_11","story_tq_11","",999,},
