        [2381] = {1901,5,1,"刘备",202,0,"此时绝不敢应命。刘备虽汉室宗亲，但功德微博，不敢领命。陶公何不让给袁绍？","此时绝不敢应命。刘备虽汉室宗亲，但功德微博，不敢领命。陶公何不让给袁绍？",1,"story_lb2_69","story_lb2_69","",999,},
