        [2383] = {1901,7,1,"刘备",202,0,"我此次带兵前来相救徐州，是为大义，绝不敢有半分私心。陶公所托，恕备不敢领受。","我此次带兵前来相救徐州，是为大义，绝不敢有半分私心。陶公所托，恕备不敢领受。",1,"story_lb2_70","story_lb2_70","",999,},
