        [2385] = {1902,1,2,"陶谦",434,0,"小英雄年少有为，胸有大志，陶谦想请小英雄帮一个忙。","小英雄年少有为，胸有大志，陶谦想请小英雄帮一个忙。",1,"story_tq_14","story_tq_14","主角",1,},
        [2386] = {1902,2,1,"主角",1,0,"请陶公直言。","请陶公直言。",1,"story_zj2_166","story_nzj2_166","",999,},
        [2387] = {1902,3,2,"陶谦",434,0,"今天下扰乱，老夫年迈，儿子不才，不堪国家重任。欲将这徐州太守之位传给刘备，谁知刘备竟不肯受，请小英雄帮忙劝劝刘备。","今天下扰乱，老夫年迈，儿子不才，不堪国家重任。欲将这徐州太守之位传给刘备，谁知刘备竟不肯受，请小英雄帮忙劝劝刘备。",1,"story_tq_15","story_tq_15","",999,},
        [2388] = {1902,4,2,"陶谦",434,0,"今天下扰乱，老夫年迈，儿子不才，不堪国家重任。欲将这徐州太守之位传给刘备，谁知刘备竟不肯受，请小英雄帮忙劝劝刘备。","今天下扰乱，老夫年迈，儿子不才，不堪国家重任。欲将这徐州太守之位传给刘备，谁知刘备竟不肯受，请小英雄帮忙劝劝刘备。",1,"story_tq_16","story_tq_16","",999,},
