        [2390] = {1902,6,2,"陶谦",434,0,"小英雄有所不知，我有两子，长子商，次子鹰，其才皆不堪任。这可怎生是好？（说罢老泪纵横）","小英雄有所不知，我有两子，长子商，次子鹰，其才皆不堪任。这可怎生是好？（说罢老泪纵横）",1,"story_tq_17","story_tq_17","",999,},
        [2391] = {1902,7,2,"陶谦",434,0,"小英雄有所不知，我有两子，长子商，次子鹰，其才皆不堪任。这可怎生是好？（说罢老泪纵横）","小英雄有所不知，我有两子，长子商，次子鹰，其才皆不堪任。这可怎生是好？（说罢老泪纵横）",1,"story_tq_18","story_tq_18","",999,},
