        [2393] = {1903,1,2,"关羽",205,0,"四弟，大哥知道你要来劝，让我和翼德拦着你。","四妹，大哥知道你要来劝，让我和翼德拦着你。",1,"story_gy1_33","story_gy1_33_b","主角",1,},
