        [2395] = {1904,1,1,"张星彩",216,0,"爹爹，你好生糊涂，拦着我主公做什么？","爹爹，你好生糊涂，拦着我主公做什么？",1,"story_zxc_174","story_zxc_174","张飞",206,},
        [2396] = {1904,2,2,"张飞",206,0,"乖女儿，军令在前，你别怪爹爹。","乖女儿，军令在前，你别怪爹爹。",1,"story_zf1_21","story_zf1_21","",999,},
        [2397] = {1904,3,1,"张星彩",216,0,"那等会战斗的时候，你可不许伤着我主公。","那等会战斗的时候，你可不许伤着我主公。",1,"story_zxc_175","story_zxc_175","",999,},
        [2398] = {1904,4,2,"张飞",206,0,"好好好，我的宝贝心肝说的话，爹爹一定照办！放心，爹爹会点到为止的。","好好好，我的宝贝心肝说的话，爹爹一定照办！放心，爹爹会点到为止的。",1,"story_zf1_22","story_zf1_22","",999,},
        [2399] = {1905,1,1,"主角",1,0,"大哥，这徐州素来为四争之地，北有曹操占据兖州，兵多粮广；南有袁术，虎视眈眈。倘若你不来接徐州之位，恐怕徐州百姓危矣。","大哥，这徐州素来为四争之地，北有曹操占据兖州，兵多粮广；南有袁术，虎视眈眈。倘若你不来接徐州之位，恐怕徐州百姓危矣。",1,"story_zj2_170","story_nzj2_170","刘备",202,},
        [2400] = {1905,2,1,"主角",1,0,"大哥，这徐州素来为四争之地，北有曹操占据兖州，兵多粮广；南有袁术，虎视眈眈。倘若你不来接徐州之位，恐怕徐州百姓危矣。","大哥，这徐州素来为四争之地，北有曹操占据兖州，兵多粮广；南有袁术，虎视眈眈。倘若你不来接徐州之位，恐怕徐州百姓危矣。",1,"story_zj2_171","story_nzj2_171","刘备",202,},
        [2401] = {1905,3,2,"刘备",202,0,"四弟所言极是，是大哥迂腐了。只不过，大哥孤身一人，安能当此大任！","四妹所言极是，是大哥迂腐了。只不过，大哥孤身一人，安能当此大任！",1,"story_lb2_72","story_lb2_72_b","",999,},
        [2402] = {1905,4,1,"主角",1,0,"徐州的孙乾、糜竺、陈登自会辅助于你，还有我们兄弟几个呢。大哥，你就别推辞了！","徐州的孙乾、糜竺、陈登自会辅助于你，还有我们兄弟几个呢。大哥，你就别推辞了！",1,"story_zj2_172","story_nzj2_172","",999,},
        [2403] = {1905,5,2,"刘备",202,0,"好，那就听四弟一言！","好，那就听四妹一言！",1,"story_lb2_73","story_lb2_73_b","",999,},
        [2404] = {1905,6,2,"刘备",202,0,"四弟，这块名将令，是陶公临死托我转送于你，感谢你让曹操退兵，解徐州之急！","四妹，这块名将令，是陶公临死托我转送于你，感谢你让曹操退兵，解徐州之急！",1,"story_lb2_74","story_lb2_74_b","",999,},
        [2405] = {1905,7,1,"主角",1,0,"谢谢大哥！四弟就不客气受领了，哈哈！","谢谢大哥！四妹就不客气受领了，哈哈！",1,"story_zj2_173","story_nzj2_173","",999,},
        [2406] = {2001,1,1,"主角",1,0,"大哥在徐州已经稳定，我辞别了大哥，来了长安，想看看长安现今怎么样了。","大哥在徐州已经稳定，我辞别了大哥，来了长安，想看看长安现今怎么样了。",1,"story_zj2_174","story_nzj2_174","",0,},
        [2407] = {2001,2,1,"主角",1,0,"啊！上次来长安杀董卓时，长安还一片繁华，现在为何如此残破凋敝！星彩，你可知发生了何事？","啊！上次来长安杀董卓时，长安还一片繁华，现在为何如此残破凋敝！星彩，你可知发生了何事？",1,"story_zj2_175","story_nzj2_175","张星彩",216,},
        [2408] = {2001,3,2,"张星彩",216,0,"主公，李傕郭汜占领长安后劫持天子，把持朝政，但互相猜忌，多次打斗，这次打了好几个月，死了几万人，把好好的长安几乎变成一片废墟。","主公，李傕郭汜占领长安后劫持天子，把持朝政，但互相猜忌，多次打斗，这次打了好几个月，死了几万人，把好好的长安几乎变成一片废墟。",1,"story_zxc_176","story_zxc_176","",999,},
        [2409] = {2001,4,2,"张星彩",216,0,"主公，李傕郭汜占领长安后劫持天子，把持朝政，但互相猜忌，多次打斗，这次打了好几个月，死了几万人，把好好的长安几乎变成一片废墟。","主公，李傕郭汜占领长安后劫持天子，把持朝政，但互相猜忌，多次打斗，这次打了好几个月，死了几万人，把好好的长安几乎变成一片废墟。",1,"story_zxc_177","story_zxc_177","",999,},
        [2410] = {2001,5,2,"关银屏",217,0,"哼，狗咬狗一嘴毛！当初那贾诩不是说会牵制李傕郭汜，保护百官与天子吗？","哼，狗咬狗一嘴毛！当初那贾诩不是说会牵制李傕郭汜，保护百官与天子吗？",1,"story_gyp_93","story_gyp_93","",999,},
