        [2525] = {39,9,1,"主角",1,0,"我已经迫不及待想获得神兵了！走，打南蛮去！","我已经迫不及待想获得神兵了！走，打南蛮去！",1,"story_zj2_206","story_nzj2_206","",999,},
        [2526] = {40,1,2,"张星彩",216,0,"哇，恭喜主公升到58级！58级可以穿戴#锦囊#了呢！","哇，恭喜主公升到58级！58级可以穿戴#锦囊#了呢！",1,"story_zxc_192","story_zxc_192","主角",1,},
        [2527] = {40,2,1,"主角",1,0,"#锦囊#？锦囊有什么用？","#锦囊#？锦囊有什么用？",1,"story_zj2_207","story_nzj2_207","",999,},
        [2528] = {40,3,2,"张星彩",216,0,"锦囊可以强化武将的特性，比如关银屏的定位是输出，穿戴关银屏锦囊就可以加攻击；貂蝉的定位是减怒，穿戴貂蝉锦囊就可以减怒。","锦囊可以强化武将的特性，比如关银屏的定位是输出，穿戴关银屏锦囊就可以加攻击；貂蝉的定位是减怒，穿戴貂蝉锦囊就可以减怒。",1,"story_zxc_193","story_zxc_193","",999,},
        [2529] = {40,4,2,"张星彩",216,0,"锦囊可以强化武将的特性，比如关银屏的定位是输出，穿戴关银屏锦囊就可以加攻击；貂蝉的定位是减怒，穿戴貂蝉锦囊就可以减怒。","锦囊可以强化武将的特性，比如关银屏的定位是输出，穿戴关银屏锦囊就可以加攻击；貂蝉的定位是减怒，穿戴貂蝉锦囊就可以减怒。",1,"story_zxc_194","story_zxc_194","",999,},
        [2530] = {40,5,2,"张星彩",216,0,"简单来说，锦囊提供了更多的特性搭配与选择，穿戴锦囊能让武将变得更加厉害。","简单来说，锦囊提供了更多的特性搭配与选择，穿戴锦囊能让武将变得更加厉害。",1,"story_zxc_195","story_zxc_195","",999,},
        [2531] = {40,6,1,"主角",1,0,"听起来，锦囊很厉害啊。","听起来，锦囊很厉害啊。",1,"story_zj2_208","story_nzj2_208","",999,},
        [2532] = {40,7,2,"张星彩",216,0,"是呢！主公，让星彩给您穿戴一个锦囊试试吧。","是呢！主公，让星彩给您穿戴一个锦囊试试吧。",1,"story_zxc_196","story_zxc_196","",999,},
        [2533] = {41,1,2,"张星彩",216,0,"好厉害！穿戴关银屏锦囊后，主公您的攻击瞬间提升了1000点！","好厉害！穿戴关银屏锦囊后，主公您的攻击瞬间提升了1000点！",1,"story_zxc_197","story_zxc_197","主角",1,},
        [2534] = {41,2,1,"主角",1,0,"这还只是紫将锦囊，那橙将锦囊岂不是更厉害？","这还只是紫将锦囊，那橙将锦囊岂不是更厉害？",1,"story_zj2_209","story_nzj2_209","",999,},
        [2535] = {41,3,2,"张星彩",216,0,"主公真聪明！锦囊的资质越高，特性也就越厉害。","主公真聪明！锦囊的资质越高，特性也就越厉害。",1,"story_zxc_198","story_zxc_198","",999,},
        [2536] = {41,4,1,"主角",1,0,"锦囊这么厉害，那在哪能获得锦囊呢？","锦囊这么厉害，那在哪能获得锦囊呢？",1,"story_zj2_210","story_nzj2_210","",999,},
        [2537] = {41,5,2,"张星彩",216,0,"主公，锦囊和官衔印一样，是通过拍卖获得的！军团boss、军团试炼、军团答题等活动都有概率出锦囊。","主公，锦囊和官衔印一样，是通过拍卖获得的！军团boss、军团试炼、军团答题等活动都有概率出锦囊。",1,"story_zxc_199","story_zxc_199","",999,},
        [2538] = {41,6,2,"张星彩",216,0,"主公，锦囊和官衔印一样，是通过拍卖获得的！军团boss、军团试炼、军团答题等活动都有概率出锦囊。","主公，锦囊和官衔印一样，是通过拍卖获得的！军团boss、军团试炼、军团答题等活动都有概率出锦囊。",1,"story_zxc_200","story_zxc_200","",999,},
        [2539] = {41,7,1,"主角",1,0,"哦，只要多多参加军团活动和拍卖，就能获得锦囊。","哦，只要多多参加军团活动和拍卖，就能获得锦囊。",1,"story_zj2_211","story_nzj2_211","",999,},
        [2540] = {41,8,2,"张星彩",216,0,"是呢，主公可以去锦囊图鉴看看，研究下如何搭配锦囊更厉害哦。","是呢，主公可以去锦囊图鉴看看，研究下如何搭配锦囊更厉害哦。",1,"story_zxc_201","story_zxc_201","",999,},
        [2541] = {41,9,1,"主角",1,0,"知道了，谢谢星彩！","知道了，谢谢星彩！",1,"story_zj2_212","story_nzj2_212","",999,},
        [2542] = {41,10,2,"张星彩",216,0,"主公客气了！星彩愿意永远为主公保驾护航！","主公客气了！星彩愿意永远为主公保驾护航！",1,"story_zxc_202","story_zxc_202","",999,},
        [2543] = {42,1,2,"关银屏",217,0,"主公您已经70级，实力威震天下，战力之高，连神兽都愿意和您并肩战斗了呢！","主公您已经70级，实力威震天下，战力之高，连神兽都愿意和您并肩战斗了呢！",1,"story_gyp_115","story_gyp_115","主角",1,},
