        [2545] = {42,3,2,"关银屏",217,0,"是的，在我们神州大地上，自古就流传着神兽的各种传说，只有天命之人和真正的强者才能见到他们的身影，我爹爹关羽就曾经得到过青龙庇佑。","是的，在我们神州大地上，自古就流传着神兽的各种传说，只有天命之人和真正的强者才能见到他们的身影，我爹爹关羽就曾经得到过青龙庇佑。",1,"story_gyp_116","story_gyp_116","",999,},
        [2546] = {42,4,2,"关银屏",217,0,"是的，在我们神州大地上，自古就流传着神兽的各种传说，只有天命之人和真正的强者才能见到他们的身影，我爹爹关羽就曾经得到过青龙庇佑。","是的，在我们神州大地上，自古就流传着神兽的各种传说，只有天命之人和真正的强者才能见到他们的身影，我爹爹关羽就曾经得到过青龙庇佑。",1,"story_gyp_117","story_gyp_117","",999,},
        [2547] = {42,5,1,"主角",1,0,"怪不得二哥这么厉害，原来有神兽庇佑！我也想要属于自己的神兽！","怪不得二哥这么厉害，原来有神兽庇佑！我也想要属于自己的神兽！",1,"story_zj2_214","story_nzj2_214","",999,},
        [2548] = {42,6,2,"关银屏",217,0,"主公别急，我们已经为您准备好了第一只神兽，现在就让银屏带您去看看吧。","主公别急，我们已经为您准备好了第一只神兽，现在就让银屏带您去看看吧。",1,"story_gyp_118","story_gyp_118","",999,},
