        [2555] = {43,7,2,"关银屏",217,0,"神兽家园可以产出兽魂，让银屏带您去看看吧。","神兽家园可以产出兽魂，让银屏带您去看看吧。",1,"story_gyp_122","story_gyp_122","",999,},
