        [2557] = {44,2,2,"关银屏",217,0,"神兽家园里有一棵神树，此树吸取天地之灵气而成，能够凝结出吸引神兽的兽魂。神树等级越高，产出的兽魂越多。","神兽家园里有一棵神树，此树吸取天地之灵气而成，能够凝结出吸引神兽的兽魂。神树等级越高，产出的兽魂越多。",1,"story_gyp_124","story_gyp_124","",999,},
        [2558] = {44,3,2,"关银屏",217,0,"神兽家园里有一棵神树，此树吸取天地之灵气而成，能够凝结出吸引神兽的兽魂。神树等级越高，产出的兽魂越多。","神兽家园里有一棵神树，此树吸取天地之灵气而成，能够凝结出吸引神兽的兽魂。神树等级越高，产出的兽魂越多。",1,"story_gyp_125","story_gyp_125","",999,},
        [2559] = {44,4,1,"主角",1,0,"我懂了！升级神树，能获得更多兽魂！有了兽魂，就可以去神兽商店兑换高级神兽。","我懂了！升级神树，能获得更多兽魂！有了兽魂，就可以去神兽商店兑换高级神兽。",1,"story_zj2_218","story_nzj2_218","",999,},
        [2560] = {44,5,1,"主角",1,0,"我懂了！升级神树，能获得更多兽魂！有了兽魂，就可以去神兽商店兑换高级神兽。","我懂了！升级神树，能获得更多兽魂！有了兽魂，就可以去神兽商店兑换高级神兽。",1,"story_zj2_219","story_nzj2_219","",999,},
        [2561] = {44,6,2,"关银屏",217,0,"主公真厉害，一下就明白了，所以主公要记得给神树升级哦！","主公真厉害，一下就明白了，所以主公要记得给神树升级哦！",1,"story_gyp_126","story_gyp_126","",999,},
