        [2563] = {44,8,2,"关银屏",217,0,"主公加油，问鼎三国，指日可待！","主公加油，问鼎三国，指日可待！",1,"story_gyp_127","story_gyp_127","",999,},
        [2564] = {2201,1,2,"曹操",103,0,"刘备屯兵徐州，自领州事；近吕布以兵败投之，备使居于小沛，若二人同心引兵来犯，实乃心腹之患。诸位可有何妙计？","刘备屯兵徐州，自领州事；近吕布以兵败投之，备使居于小沛，若二人同心引兵来犯，实乃心腹之患。诸位可有何妙计？",1,"story_cc1_234","story_cc1_234","荀彧",102,},
        [2565] = {2201,2,2,"曹操",103,0,"刘备屯兵徐州，自领州事；近吕布以兵败投之，备使居于小沛，若二人同心引兵来犯，实乃心腹之患。诸位可有何妙计？","刘备屯兵徐州，自领州事；近吕布以兵败投之，备使居于小沛，若二人同心引兵来犯，实乃心腹之患。诸位可有何妙计？",1,"story_cc1_235","story_cc1_235","荀彧",102,},
        [2566] = {2201,3,1,"荀彧",102,0,"今许都新定，不可造次用兵。彧有一计，名曰“二虎竞食”，让刘备教杀吕布。","今许都新定，不可造次用兵。彧有一计，名曰“二虎竞食”，让刘备教杀吕布。",1,"story_xy1_31","story_xy1_31","",999,},
        [2567] = {2201,4,1,"荀彧",102,0,"今许都新定，不可造次用兵。彧有一计，名曰“二虎竞食”，让刘备教杀吕布。","今许都新定，不可造次用兵。彧有一计，名曰“二虎竞食”，让刘备教杀吕布。",1,"story_xy1_32","story_xy1_32","",999,},
        [2568] = {2201,5,2,"曹操",103,0,"适才探子来报，此计已被刘备化解，刘备并未杀吕布。","适才探子来报，此计已被刘备化解，刘备并未杀吕布。",1,"story_cc1_236","story_cc1_236","",999,},
        [2569] = {2201,6,1,"荀彧",102,0,"又有一计，名曰“驱虎吞狼”之计，明公可明诏刘备讨袁术，再暗令人往袁术处报说刘备要略南郡，袁术必然怒而攻备。两边相并，吕布必生异心。","又有一计，名曰“驱虎吞狼”之计，明公可明诏刘备讨袁术，再暗令人往袁术处报说刘备要略南郡，袁术必然怒而攻备。两边相并，吕布必生异心。",1,"story_xy1_33","story_xy1_33","",999,},
        [2570] = {2201,7,1,"荀彧",102,0,"又有一计，名曰“驱虎吞狼”之计，明公可明诏刘备讨袁术，再暗令人往袁术处报说刘备要略南郡，袁术必然怒而攻备。两边相并，吕布必生异心。","又有一计，名曰“驱虎吞狼”之计，明公可明诏刘备讨袁术，再暗令人往袁术处报说刘备要略南郡，袁术必然怒而攻备。两边相并，吕布必生异心。",1,"story_xy1_34","story_xy1_34","",999,},
        [2571] = {2201,8,2,"曹操",103,0,"好计好计！就依令君所言。","好计好计！就依令君所言。",1,"story_cc1_237","story_cc1_237","",999,},
        [2572] = {2201,9,1,"主角",1,0,"不好，曹操要对付大哥！我们快去找大哥，让他不要中了曹操的奸计！","不好，曹操要对付大哥！我们快去找大哥，让他不要中了曹操的奸计！",1,"story_zj2_221","story_nzj2_221","",0,},
        [2573] = {2202,1,2,"袁术",412,0,"刘备不过是个编草鞋的，如今占据徐州，竟敢与各诸侯同列。我正要去讨伐他，他竟敢来讨伐于我！可恨！","刘备不过是个编草鞋的，如今占据徐州，竟敢与各诸侯同列。我正要去讨伐他，他竟敢来讨伐于我！可恨！",1,"story_ys2_23","story_ys2_23","刘备",202,},
        [2574] = {2202,2,1,"刘备",202,0,"吾奉天子诏，以讨不臣。汝竟敢抵抗，罪不容诛！","吾奉天子诏，以讨不臣。汝竟敢抵抗，罪不容诛！",1,"story_lb2_75","story_lb2_75","",999,},
        [2575] = {2202,3,1,"主角",1,0,"大哥，这袁术狂妄的狠，我来挫挫他的锐气！","大哥，这袁术狂妄的狠，我来挫挫他的锐气！",1,"story_zj2_222","story_nzj2_222","",999,},
