        [2579] = {2203,4,2,"刘备",202,0,"你守不住城。你一则酒后刚强，鞭挞士兵；二则做事轻易不容人，我不放心。","你守不住城。你一则酒后刚强，鞭挞士兵；二则做事轻易不容人，我不放心。",1,"story_lb2_77","story_lb2_77","",999,},
        [2580] = {2203,5,2,"张飞",206,0,"弟从今以后，不饮酒，不打军士，诸般听人劝谏便了。","弟从今以后，不饮酒，不打军士，诸般听人劝谏便了。",1,"story_zf1_24","story_zf1_24","",999,},
        [2581] = {2203,6,1,"主角",1,0,"三哥，你好好守城，我帮二哥大哥去打袁术。","三哥，你好好守城，我帮二哥大哥去打袁术。",1,"story_zj2_224","story_nzj2_224","",999,},
        [2582] = {2204,1,1,"主角",1,0,"这吕布当真可恨，竟然趁我们出兵，夺了徐州。","这吕布当真可恨，竟然趁我们出兵，夺了徐州。",1,"story_zj2_225","story_nzj2_225","刘备",202,},
        [2583] = {2204,2,2,"刘备",202,0,"四弟息怒！吕布已将两位夫人及家眷尽数送还，还送了不少粮米缎匹。此事三弟确有过错。我已经与奉先和好如初，他拒徐州，我占小沛。","四妹息怒！吕布已将两位夫人及家眷尽数送还，还送了不少粮米缎匹。此事三弟确有过错。我已经与奉先和好如初，他拒徐州，我占小沛。",1,"story_lb2_78","story_lb2_78","",999,},
        [2584] = {2204,3,2,"刘备",202,0,"四弟息怒！吕布已将两位夫人及家眷尽数送还，还送了不少粮米缎匹。此事三弟确有过错。我已经与奉先和好如初，他拒徐州，我占小沛。","四妹息怒！吕布已将两位夫人及家眷尽数送还，还送了不少粮米缎匹。此事三弟确有过错。我已经与奉先和好如初，他拒徐州，我占小沛。",1,"story_lb2_79","story_lb2_79","",999,},
        [2585] = {2204,4,2,"刘备",202,0,"此时如果我与吕布打起来，岂不正中了曹操下怀，让曹操与袁术渔翁得利。","此时如果我与吕布打起来，岂不正中了曹操下怀，让曹操与袁术渔翁得利。",1,"story_lb2_80","story_lb2_80","",999,},
        [2586] = {2204,5,1,"主角",1,0,"话虽如此，但这口气我可咽不下去。大哥，你等着，我去找吕布，给你讨回公道！","话虽如此，但这口气我可咽不下去。大哥，你等着，我去找吕布，给你讨回公道！",1,"story_zj2_226","story_nzj2_226","",999,},
        [2587] = {2205,1,2,"纪灵",435,0,"刘备何在？吾奉主公袁术之名，提十万之兵，专捉刘备！","刘备何在？吾奉主公袁术之名，提十万之兵，专捉刘备！",1,"story_jl_1","story_jl_1","主角",1,},
        [2588] = {2205,2,1,"主角",1,0,"纪灵，休要妄言！不过是我手下败将！我大哥乃剿灭黄巾的功臣，你比百万黄巾如何，竟敢伤我大哥？","纪灵，休要妄言！不过是我手下败将！我大哥乃剿灭黄巾的功臣，你比百万黄巾如何，竟敢伤我大哥？",1,"story_zj2_227","story_nzj2_227","",999,},
        [2589] = {2205,3,2,"吕布",403,0,"小英雄，且听我一言！","小英雄，且听我一言！",1,"story_lb1_61","story_lb1_61","",999,},
        [2590] = {2205,4,1,"主角",1,0,"你抢我大哥徐州城，打的就是你！","你抢我大哥徐州城，打的就是你！",1,"story_zj2_228","story_nzj2_228","",999,},
        [2591] = {2206,1,2,"吕布",403,0,"哈哈，小英雄这脾性我喜欢，但我劝你两家不要厮杀。","哈哈，小英雄这脾性我喜欢，但我劝你两家不要厮杀。",1,"story_lb1_62","story_lb1_62","主角",1,},
        [2592] = {2206,2,2,"吕布",403,0,"那辕门离中军一百五十步，吾若一箭射中戟小枝，你两家罢兵。若射不中，你各自回营，安排厮杀。","那辕门离中军一百五十步，吾若一箭射中戟小枝，你两家罢兵。若射不中，你各自回营，安排厮杀。",1,"story_lb1_63","story_lb1_63","",999,},
        [2593] = {2206,3,2,"吕布",403,0,"那辕门离中军一百五十步，吾若一箭射中戟小枝，你两家罢兵。若射不中，你各自回营，安排厮杀。","那辕门离中军一百五十步，吾若一箭射中戟小枝，你两家罢兵。若射不中，你各自回营，安排厮杀。",1,"story_lb1_64","story_lb1_64","",999,},
        [2594] = {2206,4,2,"吕布",403,0,"着！（吕布取弓箭来，一箭正射中方天画戟小枝）哈哈哈，此天令你两家罢兵。","着！（吕布取弓箭来，一箭正射中方天画戟小枝）哈哈哈，此天令你两家罢兵。",1,"story_lb1_65","story_lb1_65","",999,},
        [2595] = {2206,5,1,"主角",1,0,"真是弓开如秋月行天，箭去似流星落地！好，既然是吕布大哥说和，那便罢了。纪灵，若你再敢来进犯，休怪我不客气。","真是弓开如秋月行天，箭去似流星落地！好，既然是吕布大哥说和，那便罢了。纪灵，若你再敢来进犯，休怪我不客气。",1,"story_zj2_229","story_nzj2_229","",999,},
        [2596] = {2206,6,1,"主角",1,0,"真是弓开如秋月行天，箭去似流星落地！好，既然是吕布大哥说和，那便罢了。纪灵，若你再敢来进犯，休怪我不客气。","真是弓开如秋月行天，箭去似流星落地！好，既然是吕布大哥说和，那便罢了。纪灵，若你再敢来进犯，休怪我不客气。",1,"story_zj2_230","story_nzj2_230","",999,},
        [2597] = {2206,7,1,"纪灵",435,0,"不敢不敢！我这就退兵！","不敢不敢！我这就退兵！",1,"story_jl_2","story_jl_2","",999,},
        [2598] = {2206,8,2,"吕布",403,0,"玄德，这枚名将令就作为赔礼，徐州城奉先愿还给你！","玄德，这枚名将令就作为赔礼，徐州城奉先愿还给你！",1,"story_lb1_66","story_lb1_66","",999,},
        [2599] = {2206,9,2,"刘备",202,0,"不可。备欲让给奉先久矣，不若驻扎小沛，依然成犄角之势，抵御外敌。","不可。备欲让给奉先久矣，不若驻扎小沛，依然成犄角之势，抵御外敌。",1,"story_lb2_81","story_lb2_81","",999,},
        [2600] = {2206,10,2,"刘备",202,0,"四弟，这枚名将令你且收下。","四妹，这枚名将令你且收下。",1,"story_lb2_82","story_lb2_82_b","",999,},
        [2601] = {2206,11,1,"主角",1,0,"多谢大哥。以后有我在，那袁术定不敢再来进犯！","多谢大哥。以后有我在，那袁术定不敢再来进犯！",1,"story_zj2_231","story_nzj2_231","",999,},
        [2602] = {2301,1,2,"纪灵",435,0,"主公，这是吕布辕门射戟逼我罢兵，跟我没关系啊。","主公，这是吕布辕门射戟逼我罢兵，跟我没关系啊。",1,"story_jl_3","story_jl_3","袁术",412,},
        [2603] = {2301,2,1,"袁术",412,0,"我送了吕布那么多粮食布匹，他竟然光收钱不办事，还偏袒刘备！","我送了吕布那么多粮食布匹，他竟然光收钱不办事，还偏袒刘备！",1,"story_ys2_24","story_ys2_24","",999,},
        [2604] = {2301,3,1,"袁术",412,0,"来人，我要提重兵，亲征刘备，兼讨伐吕布。","来人，我要提重兵，亲征刘备，兼讨伐吕布。",1,"story_ys2_25","story_ys2_25","",999,},
        [2605] = {2301,4,2,"纪灵",435,0,"主公不可造次。吕布勇力过人，兼有徐州之地，若吕布与刘备首尾相连，更加麻烦。","主公不可造次。吕布勇力过人，兼有徐州之地，若吕布与刘备首尾相连，更加麻烦。",1,"story_jl_4","story_jl_4","",999,},
        [2606] = {2301,5,2,"纪灵",435,0,"我听说吕布有一个女儿，已及笄。主公有子也，可令人求亲于吕布。","我听说吕布有一个女儿，已及笄。主公有子也，可令人求亲于吕布。",1,"story_jl_5","story_jl_5","",999,},
        [2607] = {2301,6,1,"袁术",412,0,"好计策，到时候我和吕布成了亲家，再许之以利，这天下还有谁能挡我。称帝指日可待呀。","好计策，到时候我和吕布成了亲家，再许之以利，这天下还有谁能挡我。称帝指日可待呀。",1,"story_ys2_26","story_ys2_26","",999,},
        [2608] = {2302,1,1,"吕布",403,0,"袁术派人来求亲，你们怎么看待这事？","袁术派人来求亲，你们怎么看待这事？",1,"story_lb1_67","story_lb1_67","严氏",227,},
        [2609] = {2302,2,2,"严氏",227,0,"袁公路久镇淮南，兵多粮广，早晚会做皇帝。他只有一个儿子，我们女儿嫁过去，将来就是皇后啊。","袁公路久镇淮南，兵多粮广，早晚会做皇帝。他只有一个儿子，我们女儿嫁过去，将来就是皇后啊。",1,"story_ys4_1","story_ys4_1","",999,},
        [2610] = {2302,3,2,"陈宫",416,0,"今天下诸侯互相争雄，北，今公与袁公路，诸侯肯定嫉妒。如果被人伏兵夺走怎么办。即可送灵雎到寿春，先居别馆，然后择机吉成亲，可万无一失。","今天下诸侯互相争雄，北，今公与袁公路，诸侯肯定嫉妒。如果被人伏兵夺走怎么办。即可送灵雎到寿春，先居别馆，然后择机吉成亲，可万无一失。",1,"story_cg1_23","story_cg1_23","",999,},
        [2611] = {2302,4,2,"陈宫",416,0,"今天下诸侯互相争雄，北，今公与袁公路，诸侯肯定嫉妒。如果被人伏兵夺走怎么办。即可送灵雎到寿春，先居别馆，然后择机吉成亲，可万无一失。","今天下诸侯互相争雄，北，今公与袁公路，诸侯肯定嫉妒。如果被人伏兵夺走怎么办。即可送灵雎到寿春，先居别馆，然后择机吉成亲，可万无一失。",1,"story_cg1_24","story_cg1_24","",999,},
        [2612] = {2302,5,1,"吕布",403,0,"哈哈，那就让吕灵雎嫁过去把。来人，速速回报袁术，我吕布愿与他结为儿女亲家。","哈哈，那就让吕灵雎嫁过去把。来人，速速回报袁术，我吕布愿与他结为儿女亲家。",1,"story_lb1_68","story_lb1_68","",999,},
        [2613] = {2303,1,1,"陈珪",133,0,"闻将军僵尸，特来吊丧。","闻将军僵尸，特来吊丧。",1,"story_cg2_1","story_cg2_1","吕布",403,},
        [2614] = {2303,2,2,"吕布",403,0,"何出此言？","何出此言？",1,"story_lb1_69","story_lb1_69","",999,},
        [2615] = {2303,3,1,"陈珪",133,0,"袁术有称帝之意，等他称帝，则等同于造反。公与反贼结亲，到时候肯定为天下所不为容。","袁术有称帝之意，等他称帝，则等同于造反。公与反贼结亲，到时候肯定为天下所不为容。",1,"story_cg2_2","story_cg2_2","",999,},
        [2616] = {2303,4,1,"主角",1,0,"总算赶到了！吕布，听说你要将女儿嫁给袁术的儿子，万万不可！","总算赶到了！吕布，听说你要将女儿嫁给袁术的儿子，万万不可！",1,"story_zj2_232","story_nzj2_232","",999,},
        [2617] = {2303,5,2,"吕布",403,0,"小英雄为何如此说？","小英雄为何如此说？",1,"story_lb1_70","story_lb1_70","",999,},
        [2618] = {2303,6,1,"主角",1,0,"当初李傕郭汜之后，你投靠袁术，他却四处排挤于你，你忘了吗？袁术这等小人，你跟着他，将来必定招祸啊。","当初李傕郭汜之后，你投靠袁术，他却四处排挤于你，你忘了吗？袁术这等小人，你跟着他，将来必定招祸啊。",1,"story_zj2_233","story_nzj2_233","",999,},
        [2619] = {2303,7,1,"主角",1,0,"当初李傕郭汜之后，你投靠袁术，他却四处排挤于你，你忘了吗？袁术这等小人，你跟着他，将来必定招祸啊。","当初李傕郭汜之后，你投靠袁术，他却四处排挤于你，你忘了吗？袁术这等小人，你跟着他，将来必定招祸啊。",1,"story_zj2_234","story_nzj2_234","",999,},
        [2620] = {2303,8,2,"吕布",403,0,"我信你！陈宫误我！来人，速速将灵雎追回！","我信你！陈宫误我！来人，速速将灵雎追回！",1,"story_lb1_71","story_lb1_71","",999,},
        [2621] = {2303,9,1,"主角",1,0,"事不宜迟，我去！","事不宜迟，我去！",1,"story_zj2_235","story_nzj2_235","",999,},
        [2622] = {2304,1,2,"貂蝉",404,0,"奉先，小英雄已将灵雎追回！","奉先，小英雄已将灵雎追回！",1,"story_dc1_38","story_dc1_38","主角",1,},
        [2623] = {2304,2,1,"主角",1,0,"貂蝉姑娘拜托之事，在下自当竭力照搬！","貂蝉姑娘拜托之事，在下自当竭力照搬！",1,"story_zj2_236","story_nzj2_236","",999,},
        [2624] = {2304,3,2,"貂蝉",404,0,"奉先，妾身以为，婚嫁之事，虽有父母之命媒妁之言，但灵雎的意愿也不可忽视。她若不愿意嫁，岂不误了终身","奉先，妾身以为，婚嫁之事，虽有父母之命媒妁之言，但灵雎的意愿也不可忽视。她若不愿意嫁，岂不误了终身",1,"story_dc1_39","story_dc1_39","",999,},
        [2625] = {2304,4,2,"吕布",403,0,"蝉儿所言极是。","蝉儿所言极是。",1,"story_lb1_72","story_lb1_72","",999,},
        [2626] = {2304,5,1,"主角",1,0,"吕布，你乃当时豪杰，何必用自己女儿做前程赌注。","吕布，你乃当时豪杰，何必用自己女儿做前程赌注。",1,"story_zj2_237","story_nzj2_237","",999,},
        [2627] = {2304,6,2,"吕布",403,0,"说得对！我吕布虎步江淮，何惧天下任何人！我吕布的女儿，自然是想嫁就嫁，想不嫁就不嫁！这就去问灵雎的意思。","说得对！我吕布虎步江淮，何惧天下任何人！我吕布的女儿，自然是想嫁就嫁，想不嫁就不嫁！这就去问灵雎的意思。",1,"story_lb1_73","story_lb1_73","",999,},
        [2628] = {2304,7,2,"吕布",403,0,"说得对！我吕布虎步江淮，何惧天下任何人！我吕布的女儿，自然是想嫁就嫁，想不嫁就不嫁！这就去问灵雎的意思。","说得对！我吕布虎步江淮，何惧天下任何人！我吕布的女儿，自然是想嫁就嫁，想不嫁就不嫁！这就去问灵雎的意思。",1,"story_lb1_74","story_lb1_74","",999,},
        [2629] = {2305,1,2,"吕布",403,0,"小英雄这块名将令你且收下，若不是你，险些误了大事！","小英雄这块名将令你且收下，若不是你，险些误了大事！",1,"story_lb1_75","story_lb1_75","主角",1,},
        [2630] = {2305,2,1,"主角",1,0,"好说好说！多谢啦！","好说好说！多谢啦！",1,"story_zj2_238","story_nzj2_238","",999,},
        [2631] = {2305,3,2,"吕玲绮",408,0,"爹爹，我才不要嫁给袁术的儿子我要嫁给一个大英雄，才不要嫁给小怂包！","爹爹，我才不要嫁给袁术的儿子我要嫁给一个大英雄，才不要嫁给小怂包！",1,"story_lql_5","story_lql_5","",999,},
        [2632] = {2305,4,2,"吕布",403,0,"灵雎，爹爹以后再也不强迫你嫁人了。你说，你想嫁给哪位英雄？","灵雎，爹爹以后再也不强迫你嫁人了。你说，你想嫁给哪位英雄？",1,"story_lb1_76","story_lb1_76","",999,},
        [2633] = {2305,5,2,"吕玲绮",408,0,"我听说天子新封了一位扬威将军，年少有为，武艺高强，我想嫁给他！","我不想嫁人！听说天子新封了一位扬威将军，年少有为，英姿飒爽，我想与她结为姐妹共闯天下！",1,"story_lql_6","story_lql_6","",999,},
        [2634] = {2305,6,2,"吕玲绮",408,0,"我听说天子新封了一位扬威将军，年少有为，武艺高强，我想嫁给他！","我不想嫁人！听说天子新封了一位扬威将军，年少有为，英姿飒爽，我想与她结为姐妹共闯天下！",1,"story_lql_7","story_lql_7_b","",999,},
        [2635] = {2305,7,1,"主角",1,0,"啊，你想嫁给我？（那样的话，那貂蝉岂不是成了我娘亲，万万不可！）","什么？你想与我结拜结脉？（那样的话，貂蝉姐姐岂不是成了我娘亲，万万不可！）",1,"story_zj2_239","story_nzj2_239_b","",999,},
        [2636] = {2305,8,1,"主角",1,0,"多谢姑娘美意，我还年轻，不想成亲，告辞！","多谢姑娘美意，我还有要事在身，告辞！",1,"story_zj2_240","story_nzj2_240_b","",999,},
        [2637] = {2305,9,2,"吕玲绮",408,0,"扬威将军，你别逃，就算你逃到天涯海角，我也要嫁给你！","扬威将军，你别逃，就算你逃到天涯海角，我也要和你做姐妹！",1,"story_lql_8","story_lql_8_b","",999,},
