        [2669] = {2502,1,1,"主角",1,0,"孙兄，好久不见，怎么看着不太高兴啊！","孙兄，好久不见，怎么看着不太高兴啊！",1,"story_zj2_253","story_nzj2_253","孙策",301,},
        [2670] = {2502,2,2,"孙策",301,0,"袁术为人反复无常，言而无信，他许诺人名我为九江太守，却改用自己的亲信。后面又派我打庐江太守陆康，说要人名我为庐江太守，结果又改用自己的亲信刘勋。真是太让人失望了。","袁术为人反复无常，言而无信，他许诺人名我为九江太守，却改用自己的亲信。后面又派我打庐江太守陆康，说要人名我为庐江太守，结果又改用自己的亲信刘勋。真是太让人失望了。",1,"story_sc_53","story_sc_53","",999,},
        [2671] = {2502,3,2,"孙策",301,0,"袁术为人反复无常，言而无信，他许诺人名我为九江太守，却改用自己的亲信。后面又派我打庐江太守陆康，说要人名我为庐江太守，结果又改用自己的亲信刘勋。真是太让人失望了。","袁术为人反复无常，言而无信，他许诺人名我为九江太守，却改用自己的亲信。后面又派我打庐江太守陆康，说要人名我为庐江太守，结果又改用自己的亲信刘勋。真是太让人失望了。",1,"story_sc_54","story_sc_54","",999,},
        [2672] = {2502,4,2,"孙策",301,0,"我父亲孙坚如此英雄，我却寄人篱下，不能继承父亲的志向啊。","我父亲孙坚如此英雄，我却寄人篱下，不能继承父亲的志向啊。",1,"story_sc_55","story_sc_55","",999,},
        [2673] = {2502,5,1,"主角",1,0,"孙策，那袁术不是什么好人，又如此言而无信。不如召集你父亲的旧部，趁机收取江东，往江东打下一片基业。","孙策，那袁术不是什么好人，又如此言而无信。不如召集你父亲的旧部，趁机收取江东，往江东打下一片基业。",1,"story_zj2_254","story_nzj2_254","",999,},
        [2674] = {2502,6,2,"孙策",301,0,"只怕袁术不肯放我！","只怕袁术不肯放我！",1,"story_sc_56","story_sc_56","",999,},
        [2675] = {2502,7,1,"主角",1,0,"袁术一直想当皇帝，你把你父亲留下的传国玉玺送给袁术，他自会同意。","袁术一直想当皇帝，你把你父亲留下的传国玉玺送给袁术，他自会同意。",1,"story_zj2_255","story_nzj2_255","",999,},
