        [2679] = {2503,2,2,"关银屏",217,0,"主公的手下败将，也敢称自己是所有人的爸爸，看打！","主公的手下败将，也敢称自己是所有人的爸爸，看打！",1,"story_gyp_131","story_gyp_131","",999,},
