        [2726] = {2605,1,1,"主角",1,0,"就剩下太史慈了。孙兄，我去帮你迎战太史慈！","就剩下太史慈了。孙兄，我去帮你迎战太史慈！",1,"story_zj2_276","story_nzj2_276","孙策",301,},
        [2727] = {2605,2,2,"孙策",301,0,"上次神亭与太史慈一战，当时我抢得他背后手戟，而太史慈则夺去我的头盔，还未打得尽兴胜负未分，怎能轻易让给你？","上次神亭与太史慈一战，当时我抢得他背后手戟，而太史慈则夺去我的头盔，还未打得尽兴胜负未分，怎能轻易让给你？",1,"story_sc_74","story_sc_74","",999,},
        [2728] = {2605,3,2,"孙策",301,0,"上次神亭与太史慈一战，当时我抢得他背后手戟，而太史慈则夺去我的头盔，还未打得尽兴胜负未分，怎能轻易让给你？","上次神亭与太史慈一战，当时我抢得他背后手戟，而太史慈则夺去我的头盔，还未打得尽兴胜负未分，怎能轻易让给你？",1,"story_sc_75","story_sc_75","",999,},
