        [2731] = {2606,1,2,"太史慈",305,0,"扬威将军果然厉害，某佩服之极，这枚名将令愿送给小将军。","扬威将军果然厉害，某佩服之极，这枚名将令愿送给小将军。",1,"story_tsc_19","story_tsc_19","主角",1,},
        [2732] = {2606,2,1,"主角",1,0,"多谢多谢！","多谢多谢！",1,"story_zj2_278","story_nzj2_278","",999,},
        [2733] = {2606,3,2,"孙策",301,0,"子义真丈夫也！刘繇蠢辈，竟不能用为大将，才会大败。子义今后之路，我当与卿共闯。","子义真丈夫也！刘繇蠢辈，竟不能用为大将，才会大败。子义今后之路，我当与卿共闯。",1,"story_sc_77","story_sc_77","",999,},
        [2734] = {2606,4,2,"太史慈",305,0,"刘君新破，士卒离心，某欲自往收拾余众，以助明公。","刘君新破，士卒离心，某欲自往收拾余众，以助明公。",1,"story_tsc_20","story_tsc_20","",999,},
