        [2779] = {2802,2,2,"曹操",103,0,"小兄弟你有所不知，最近蝗灾严重，我军中也没多少粮食，况且刚与吕布交战，我这边也正在发愁呢？","小兄弟你有所不知，最近蝗灾严重，我军中也没多少粮食，况且刚与吕布交战，我这边也正在发愁呢？",1,"story_cc1_238","story_cc1_238","主角",1,},
