        [2843] = {3003,3,1,"丁夫人",227,0,"不！","不！",1,"story_dfr_2","story_dfr_2","曹操",103,},
        [2844] = {3003,4,2,"曹操",103,0,"夫人，跟我回去可好？","夫人，跟我回去可好？",1,"story_cc1_249","story_cc1_249","丁夫人",227,},
        [2845] = {3003,5,1,"丁夫人",227,0,"不！","不！",1,"story_dfr_3","story_dfr_3","曹操",103,},
        [2846] = {3003,6,2,"曹操",103,0,"我一生做事，没有什么后悔的。假如死后还有灵的话，曹昂如果问我他的母亲在哪，我将怎么回答啊！","我一生做事，没有什么后悔的。假如死后还有灵的话，曹昂如果问我他的母亲在哪，我将怎么回答啊！",1,"story_cc1_250","story_cc1_250","丁夫人",227,},
        [2847] = {3004,1,2,"夏侯惇",105,0,"扬威将军，我敬你和丞相亲厚，但你何故打杀我统领的青州兵？","扬威将军，我敬你和丞相亲厚，但你何故打杀我统领的青州兵？",1,"story_xhd_17","story_xhd_17","主角",1,},
        [2848] = {3004,2,1,"主角",1,0,"夏侯将军，竟敢来反问我？你的青州军，当初是我帮丞相收服的黄巾余党。","夏侯将军，竟敢来反问我？你的青州军，当初是我帮丞相收服的黄巾余党。",1,"story_zj2_308","story_nzj2_308","夏侯惇",105,},
        [2849] = {3004,3,1,"主角",1,0,"我从宛城营救丞相，沿途却看到青州军劫掠民家，我便杀了！怎滴，将军不服？","我从宛城营救丞相，沿途却看到青州军劫掠民家，我便杀了！怎滴，将军不服？",1,"story_zj2_309","story_nzj2_309","夏侯惇",105,},
        [2850] = {3004,4,2,"夏侯惇",105,0,"夏侯惇鲁莽！竟有如此事情，都怪夏侯惇治军不严！","夏侯惇鲁莽！竟有如此事情，都怪夏侯惇治军不严！",1,"story_xhd_18","story_xhd_18","主角",1,},
        [2851] = {3005,1,2,"曹操",103,0,"于禁，青州兵说你趁宛城之事，趁机造反，你可知罪！","于禁，青州兵说你趁宛城之事，趁机造反，你可知罪！",1,"story_cc1_251","story_cc1_251","于禁",119,},
        [2852] = {3005,2,1,"主角",1,0,"曹兄，此事乃天大的冤枉！","曹兄，此事乃天大的冤枉！",1,"story_zj2_310","story_nzj2_310","曹操",103,},
        [2853] = {3005,3,2,"曹操",103,0,"哦？","哦？",1,"story_cc1_252","story_cc1_252","主角",1,},
        [2854] = {3005,4,1,"主角",1,0,"那青州黄巾原本是黄巾贼，你可还记得。他们中有些人贼性不改，趁乱打家劫舍，我和于禁将军见到，便狠狠教训了这些人。","那青州黄巾原本是黄巾贼，你可还记得。他们中有些人贼性不改，趁乱打家劫舍，我和于禁将军见到，便狠狠教训了这些人。",1,"story_zj2_311","story_nzj2_311","曹操",103,},
        [2855] = {3005,5,1,"主角",1,0,"那青州黄巾原本是黄巾贼，你可还记得。他们中有些人贼性不改，趁乱打家劫舍，我和于禁将军见到，便狠狠教训了这些人。","那青州黄巾原本是黄巾贼，你可还记得。他们中有些人贼性不改，趁乱打家劫舍，我和于禁将军见到，便狠狠教训了这些人。",1,"story_zj2_312","story_nzj2_312","曹操",103,},
        [2856] = {3005,6,2,"曹操",103,0,"原来如此。于禁，险些错怪了你，为何不早些向我报备呢？","原来如此。于禁，险些错怪了你，为何不早些向我报备呢？",1,"story_cc1_253","story_cc1_253","于禁",119,},
        [2857] = {3005,7,1,"于禁",119,0,"当时情况紧急，敌人在后面追来，我首先抗敌，曹公是明智的人，正是谣言止于智者。","当时情况紧急，敌人在后面追来，我首先抗敌，曹公是明智的人，正是谣言止于智者。",1,"story_yj3_6","story_yj3_6","曹操",103,},
        [2858] = {3005,8,1,"于禁",119,0,"当时情况紧急，敌人在后面追来，我首先抗敌，曹公是明智的人，正是谣言止于智者。","当时情况紧急，敌人在后面追来，我首先抗敌，曹公是明智的人，正是谣言止于智者。",1,"story_yj3_7","story_yj3_7","曹操",103,},
        [2859] = {3005,9,2,"曹操",103,0,"将军在匆忙之中，能整兵坚垒，任谤任劳，使反败为胜，虽古之名将，何以加兹！","将军在匆忙之中，能整兵坚垒，任谤任劳，使反败为胜，虽古之名将，何以加兹！",1,"story_cc1_254","story_cc1_254","主角",1,},
        [2860] = {3005,10,2,"曹操",103,0,"将军在匆忙之中，能整兵坚垒，任谤任劳，使反败为胜，虽古之名将，何以加兹！","将军在匆忙之中，能整兵坚垒，任谤任劳，使反败为胜，虽古之名将，何以加兹！",1,"story_cc1_255","story_cc1_255","主角",1,},
        [2861] = {3005,11,1,"主角",1,0,"曹兄英明！","曹兄英明！",1,"story_zj2_313","story_nzj2_313","曹操",103,},
