        [2863] = {3101,1,1,"袁术",412,0,"昔汉高祖不过泗上一亭长，而有天下；今历年四百，气数已尽，海内鼎沸。","昔汉高祖不过泗上一亭长，而有天下；今历年四百，气数已尽，海内鼎沸。",1,"story_ys2_28","story_ys2_28","阁象",133,},
        [2864] = {3101,2,1,"袁术",412,0,"吾家四世三公，百姓所归；吾效应天顺人，正位九五。尔众人以为何如？","吾家四世三公，百姓所归；吾效应天顺人，正位九五。尔众人以为何如？",1,"story_ys2_29","story_ys2_29","阁象",133,},
        [2865] = {3101,3,2,"阁象",133,0,"不可。昔周后稷积德累功，至于文王，三分天下有其二，犹以服事殷。明公家世虽贵，未若有周之盛；汉室虽微，未若殷纣之暴也。此事决不可行。","不可。昔周后稷积德累功，至于文王，三分天下有其二，犹以服事殷。明公家世虽贵，未若有周之盛；汉室虽微，未若殷纣之暴也。此事决不可行。",1,"story_gx1_1","story_gx1_1","袁术",412,},
        [2866] = {3101,4,2,"阁象",133,0,"不可。昔周后稷积德累功，至于文王，三分天下有其二，犹以服事殷。明公家世虽贵，未若有周之盛；汉室虽微，未若殷纣之暴也。此事决不可行。","不可。昔周后稷积德累功，至于文王，三分天下有其二，犹以服事殷。明公家世虽贵，未若有周之盛；汉室虽微，未若殷纣之暴也。此事决不可行。",1,"story_gx1_2","story_gx1_2","袁术",412,},
        [2867] = {3101,5,1,"袁术",412,0,"吾袁姓出于陈。陈乃大舜之后。以土承火，正应其运。又谶云：代汉者，当涂高也。吾字公路，正应其谶。","吾袁姓出于陈。陈乃大舜之后。以土承火，正应其运。又谶云：代汉者，当涂高也。吾字公路，正应其谶。",1,"story_ys2_30","story_ys2_30","阁象",133,},
        [2868] = {3101,6,1,"袁术",412,0,"吾袁姓出于陈。陈乃大舜之后。以土承火，正应其运。又谶云：代汉者，当涂高也。吾字公路，正应其谶。","吾袁姓出于陈。陈乃大舜之后。以土承火，正应其运。又谶云：代汉者，当涂高也。吾字公路，正应其谶。",1,"story_ys2_31","story_ys2_31","阁象",133,},
        [2869] = {3101,7,1,"袁术",412,0,"又有传国玉玺。若不为君，背天道也。吾意已决，多言者斩！","又有传国玉玺。若不为君，背天道也。吾意已决，多言者斩！",1,"story_ys2_32","story_ys2_32","阁象",133,},
        [2870] = {3102,1,2,"张星彩",216,0,"主公，听说那袁术胆大妄为，在寿春要称帝！","主公，听说那袁术胆大妄为，在寿春要称帝！",1,"story_zxc_223","story_zxc_223","主角",1,},
        [2871] = {3102,2,2,"关银屏",217,0,"袁术此人一向心术不正，竟然要称帝！呵呵哒！我家主公可是大汉天子亲封的扬威将军！","袁术此人一向心术不正，竟然要称帝！呵呵哒！我家主公可是大汉天子亲封的扬威将军！",1,"story_gyp_145","story_gyp_145","主角",1,},
        [2872] = {3102,3,1,"主角",1,0,"袁术这个便宜皇帝要是能坐稳，我就脱光了在洛阳城上爬三圈！哼！","袁术这个便宜皇帝要是能坐稳，我就脱光了在洛阳城上爬三圈！哼！",1,"story_zj2_314","story_nzj2_314","关银屏",217,},
        [2873] = {3102,4,2,"张星彩",216,0,"主公，现在天气很冷，你脱光了在洛阳城上爬，会冻死的！","主公，现在天气很冷，你脱光了在洛阳城上爬，会冻死的！",1,"story_zxc_224","story_zxc_224","主角",1,},
