        [2876] = {3103,1,1,"主角",1,0,"袁术，你妄自称帝，被天下诸侯所不齿，我已联合曹操、刘备与吕布，三路大军正向向寿春开进，你必将成为众矢之的！","袁术，你妄自称帝，被天下诸侯所不齿，我已联合曹操、刘备与吕布，三路大军正向向寿春开进，你必将成为众矢之的！",1,"story_zj2_316","story_nzj2_316","袁术",412,},
        [2877] = {3103,2,1,"主角",1,0,"袁术，你妄自称帝，被天下诸侯所不齿，我已联合曹操、刘备与吕布，三路大军正向向寿春开进，你必将成为众矢之的！","袁术，你妄自称帝，被天下诸侯所不齿，我已联合曹操、刘备与吕布，三路大军正向向寿春开进，你必将成为众矢之的！",1,"story_zj2_317","story_nzj2_317","袁术",412,},
