        [2880] = {3104,2,2,"关银屏",217,0,"这袁术，真是搞笑！主公你看他，打不赢就跑了！好在没白来，得了一块名将令！","这袁术，真是搞笑！主公你看他，打不赢就跑了！好在没白来，得了一块名将令！",1,"story_gyp_148","story_gyp_148","主角",1,},
        [2881] = {3104,3,1,"主角",1,0,"这袁术虽然被我们打败了，但他手底下势力范围还是很大，我们还得想想办法才行。","这袁术虽然被我们打败了，但他手底下势力范围还是很大，我们还得想想办法才行。",1,"story_zj2_318","story_nzj2_318","关银屏",217,},
        [2882] = {3104,4,2,"关银屏",217,0,"袁术占据了富庶的南阳、汝南和江淮一带，地盘大，人口多，钱粮充足，得想办法削弱他的势力才行。","袁术占据了富庶的南阳、汝南和江淮一带，地盘大，人口多，钱粮充足，得想办法削弱他的势力才行。",1,"story_gyp_149","story_gyp_149","主角",1,},
        [2883] = {3104,5,2,"关银屏",217,0,"袁术占据了富庶的南阳、汝南和江淮一带，地盘大，人口多，钱粮充足，得想办法削弱他的势力才行。","袁术占据了富庶的南阳、汝南和江淮一带，地盘大，人口多，钱粮充足，得想办法削弱他的势力才行。",1,"story_gyp_150","story_gyp_150","主角",1,},
