        [2885] = {3104,7,2,"关银屏",217,0,"当初孙坚从洛阳找到玉玺，带到了江东，后面被袁术抢了去，孙策原本就对袁术有一肚子意见！","当初孙坚从洛阳找到玉玺，带到了江东，后面被袁术抢了去，孙策原本就对袁术有一肚子意见！",1,"story_gyp_151","story_gyp_151","主角",1,},
