        [4168] = {7202,17,2,"司马徽",226,0,"无孑遗者，不久则景升将逝，文武零落无孑遗矣，天命有归，龙向天飞，盖应在将军也。","无孑遗者，不久则景升将逝，文武零落无孑遗矣，天命有归，龙向天飞，盖应在将军也。",1,"story_smh_10","story_smh_10","刘备",202,},
        [4169] = {7202,18,2,"司马徽",226,0,"无孑遗者，不久则景升将逝，文武零落无孑遗矣，天命有归，龙向天飞，盖应在将军也。","无孑遗者，不久则景升将逝，文武零落无孑遗矣，天命有归，龙向天飞，盖应在将军也。",1,"story_smh_11","story_smh_11","刘备",202,},
