        [4175] = {7203,2,2,"徐庶",212,0,"久闻刘景升善善恶恶，特往谒之。及至相见，徒有虚名，盖善善而不能用，恶恶而不能去者也。故遗书别之，而来至此。","久闻刘景升善善恶恶，特往谒之。及至相见，徒有虚名，盖善善而不能用，恶恶而不能去者也。故遗书别之，而来至此。",1,"story_xs1_14","story_xs1_14","司马徽",226,},
        [4176] = {7203,3,2,"徐庶",212,0,"久闻刘景升善善恶恶，特往谒之。及至相见，徒有虚名，盖善善而不能用，恶恶而不能去者也。故遗书别之，而来至此。","久闻刘景升善善恶恶，特往谒之。及至相见，徒有虚名，盖善善而不能用，恶恶而不能去者也。故遗书别之，而来至此。",1,"story_xs1_15","story_xs1_15","司马徽",226,},
        [4177] = {7203,4,1,"司马徽",226,0,"公怀王佐之才，宜择人而事，奈何轻身往见景升乎？且英雄豪杰，只在眼前，公自不识耳。","公怀王佐之才，宜择人而事，奈何轻身往见景升乎？且英雄豪杰，只在眼前，公自不识耳。",1,"story_smh_15","story_smh_15","徐庶",212,},
