        [4196] = {7206,1,2,"徐庶",212,0,"天地反覆兮，火欲殂，大厦将崩兮，一木难扶。山谷有贤兮，欲投明主，明主求贤兮，却不知吾。","天地反覆兮，火欲殂，大厦将崩兮，一木难扶。山谷有贤兮，欲投明主，明主求贤兮，却不知吾。",1,"story_xs1_17","story_xs1_17","刘备",202,},
        [4197] = {7206,2,2,"徐庶",212,0,"天地反覆兮，火欲殂，大厦将崩兮，一木难扶。山谷有贤兮，欲投明主，明主求贤兮，却不知吾。","天地反覆兮，火欲殂，大厦将崩兮，一木难扶。山谷有贤兮，欲投明主，明主求贤兮，却不知吾。",1,"story_xs1_18","story_xs1_18","刘备",202,},
