        [4199] = {7206,4,2,"徐庶",212,0,"某乃颍上人也，姓单，名福。久闻使君纳士招贤，欲来投托，未敢辄造，故行歌于市，以动尊听耳。","某乃颍上人也，姓单，名福。久闻使君纳士招贤，欲来投托，未敢辄造，故行歌于市，以动尊听耳。",1,"story_xs1_19","story_xs1_19","刘备",202,},
        [4200] = {7206,5,2,"徐庶",212,0,"某乃颍上人也，姓单，名福。久闻使君纳士招贤，欲来投托，未敢辄造，故行歌于市，以动尊听耳。","某乃颍上人也，姓单，名福。久闻使君纳士招贤，欲来投托，未敢辄造，故行歌于市，以动尊听耳。",1,"story_xs1_20","story_xs1_20","刘备",202,},
        [4201] = {7206,6,2,"徐庶",212,0,"适使君所乘之马，再乞一观。","适使君所乘之马，再乞一观。",1,"story_xs1_21","story_xs1_21","刘备",202,},
        [4202] = {7206,7,2,"徐庶",212,0,"此非的卢马乎？虽是千里马，却只妨主，不可乘也。","此非的卢马乎？虽是千里马，却只妨主，不可乘也。",1,"story_xs1_22","story_xs1_22","刘备",202,},
        [4203] = {7206,8,1,"刘备",202,0,"此马助我跃檀溪，救吾一命。","此马助我跃檀溪，救吾一命。",1,"story_lb2_196","story_lb2_196","徐庶",212,},
        [4204] = {7206,9,2,"徐庶",212,0,"此乃救主，非妨主也，终必妨一主，某有一法可禳。","此乃救主，非妨主也，终必妨一主，某有一法可禳。",1,"story_xs1_23","story_xs1_23","刘备",202,},
        [4205] = {7206,10,1,"刘备",202,0,"愿闻禳法。","愿闻禳法。",1,"story_lb2_197","story_lb2_197","徐庶",212,},
        [4206] = {7206,11,2,"徐庶",212,0,"公意中有仇怨之人，可将此马赐之，待妨过了此人，然后乘之，自然无事。","公意中有仇怨之人，可将此马赐之，待妨过了此人，然后乘之，自然无事。",1,"story_xs1_24","story_xs1_24","刘备",202,},
        [4207] = {7206,12,1,"刘备",202,0,"公初至此，不教吾以正道，便教作利己妨人之事，备不敢闻教。","公初至此，不教吾以正道，便教作利己妨人之事，备不敢闻教。",1,"story_lb2_198","story_lb2_198","徐庶",212,},
        [4208] = {7206,13,2,"徐庶",212,0,"向闻使君仁德，未敢便信，故以此言相试耳。","向闻使君仁德，未敢便信，故以此言相试耳。",1,"story_xs1_25","story_xs1_25","刘备",202,},
        [4209] = {7206,14,1,"刘备",202,0,"备安能有仁德及人，惟先生教之。","备安能有仁德及人，惟先生教之。",1,"story_lb2_199","story_lb2_199","徐庶",212,},
        [4210] = {7206,15,2,"徐庶",212,0,"吾自颍上来此，闻新野之人歌曰，新野牧，刘皇叔，自到此，民丰足。可见使君之仁德及人也。","吾自颍上来此，闻新野之人歌曰，新野牧，刘皇叔，自到此，民丰足。可见使君之仁德及人也。",1,"story_xs1_26","story_xs1_26","刘备",202,},
        [4211] = {7206,16,2,"徐庶",212,0,"吾自颍上来此，闻新野之人歌曰，新野牧，刘皇叔，自到此，民丰足。可见使君之仁德及人也。","吾自颍上来此，闻新野之人歌曰，新野牧，刘皇叔，自到此，民丰足。可见使君之仁德及人也。",1,"story_xs1_27","story_xs1_27","刘备",202,},
        [4212] = {7206,17,1,"刘备",202,0,"玄德幸得先生相助，今日起拜汝为军师，调练本部人马。","玄德幸得先生相助，今日起拜汝为军师，调练本部人马。",1,"story_lb2_200","story_lb2_200","徐庶",212,},
        [4213] = {7301,1,2,"徐庶",212,0,"曹仁屯兵樊城，必起大军来战。","曹仁屯兵樊城，必起大军来战。",1,"story_xs1_28","story_xs1_28","刘备",202,},
        [4214] = {7301,2,1,"刘备",202,0,"当何以迎之？","当何以迎之？",1,"story_lb2_201","story_lb2_201","徐庶",212,},
        [4215] = {7301,3,2,"徐庶",212,0,"彼若尽提兵而来，樊城空虚，可乘间夺之。","彼若尽提兵而来，樊城空虚，可乘间夺之。",1,"story_xs1_29","story_xs1_29","刘备",202,},
        [4216] = {7301,4,1,"刘备",202,0,"何计应对？","何计应对？",1,"story_lb2_202","story_lb2_202","徐庶",212,},
        [4217] = {7301,5,2,"徐庶",212,0,"烦请玄德兄附耳。","烦请玄德兄附耳。",1,"story_xs1_30","story_xs1_30","刘备",202,},
        [4218] = {7302,1,1,"刘备",202,0,"曹军鸣鼓进军，布成一阵。","曹军鸣鼓进军，布成一阵。",1,"story_lb2_203","story_lb2_203","徐庶",212,},
        [4219] = {7302,2,1,"刘备",202,0,"识吾阵势？","识吾阵势？",1,"story_lb2_204","story_lb2_204","徐庶",212,},
        [4220] = {7302,3,2,"徐庶",212,0,"此八门金锁阵也。八门者：休、生、伤、杜、景、死、惊、开。","此八门金锁阵也。八门者：休、生、伤、杜、景、死、惊、开。",1,"story_xs1_31","story_xs1_31","刘备",202,},
        [4221] = {7302,4,2,"徐庶",212,0,"如从生门、景门、开门而入则吉；从伤门、惊门、休门而入则伤；从杜门、死们而人则亡。","如从生门、景门、开门而入则吉；从伤门、惊门、休门而入则伤；从杜门、死们而人则亡。",1,"story_xs1_32","story_xs1_32","刘备",202,},
        [4222] = {7302,5,2,"徐庶",212,0,"今八门虽布得整齐，只是中间通欠主持。如从东南角上生门击人，往正西景门而出，其阵必乱。","今八门虽布得整齐，只是中间通欠主持。如从东南角上生门击人，往正西景门而出，其阵必乱。",1,"story_xs1_33","story_xs1_33","刘备",202,},
        [4223] = {7302,6,1,"刘备",202,0,"传令，军士把住阵角，扬威将军引五百军从东南而入，径往西出。","传令，军士把住阵角，扬威将军引五百军从东南而入，径往西出。",1,"story_lb2_205","story_lb2_205","徐庶",212,},
        [4224] = {7302,7,2,"主角",1,0,"好，我这就领兵破了那八门金锁阵。","好，我这就领兵破了那八门金锁阵。",1,"story_zj2_477","story_nzj2_477","刘备",202,},
        [4225] = {7303,1,1,"曹仁",106,0,"刘备军中必有能者，吾阵竟为所破。","刘备军中必有能者，吾阵竟为所破。",1,"story_cr_19","story_cr_19","李典",124,},
        [4226] = {7303,2,2,"李典",124,0,"吾虽在此，甚忧樊城。","吾虽在此，甚忧樊城。",1,"story_ld1_3","story_ld1_3","曹仁",106,},
        [4227] = {7303,3,1,"曹仁",106,0,"今晚去劫寨！如得胜，再作计议，如不胜，便退军回樊城。","今晚去劫寨！如得胜，再作计议，如不胜，便退军回樊城。",1,"story_cr_20","story_cr_20","李典",124,},
        [4228] = {7303,4,2,"李典",124,0,"不可，刘备必有准备。","不可，刘备必有准备。",1,"story_ld1_4","story_ld1_4","曹仁",106,},
        [4229] = {7303,5,1,"曹仁",106,0,"若如此多疑，何以用兵！","若如此多疑，何以用兵！",1,"story_cr_21","story_cr_21","李典",124,},
        [4230] = {7304,1,2,"徐庶",212,0,"今夜曹仁必来劫寨。","今夜曹仁必来劫寨。",1,"story_xs1_34","story_xs1_34","刘备",202,},
        [4231] = {7304,2,1,"刘备",202,0,"何以敌之？","何以敌之？",1,"story_lb2_206","story_lb2_206","徐庶",212,},
        [4232] = {7304,3,2,"徐庶",212,0,"吾已预算定。","吾已预算定。",1,"story_xs1_35","story_xs1_35","刘备",202,},
        [4233] = {7304,4,2,"徐庶",212,0,"云长疾行樊城外静候，待曹仁出城后迅速夺城。","云长疾行樊城外静候，待曹仁出城后迅速夺城。",1,"story_xs1_36","story_xs1_36","刘备",202,},
        [4234] = {7304,5,2,"徐庶",212,0,"尔等留空寨，赵云与扬威将军埋伏寨外，奇袭前来的曹仁大军，将其逼至河边。","尔等留空寨，赵云与扬威将军埋伏寨外，奇袭前来的曹仁大军，将其逼至河边。",1,"story_xs1_37","story_xs1_37","刘备",202,},
