        [4295] = {7310,12,2,"徐庶",212,0,"此人乃琅琊阳都人，覆姓诸葛，名亮，字孔明，乃汉司隶校尉诸葛丰之后。","此人乃琅琊阳都人，覆姓诸葛，名亮，字孔明，乃汉司隶校尉诸葛丰之后。",1,"story_xs1_61","story_xs1_61","刘备",202,},
