        [4307] = {7312,1,1,"刘备",202,0,"汉室末胄、涿郡愚夫，久闻先生大名，如雷贯耳。","汉室末胄、涿郡愚夫，久闻先生大名，如雷贯耳。",1,"story_lb2_224","story_lb2_224","诸葛亮",203,},
