        [4311] = {7312,5,2,"诸葛亮",203,0,"昨观书意，足见将军忧民忧国之心，但恨亮年幼才疏，有误下问。","昨观书意，足见将军忧民忧国之心，但恨亮年幼才疏，有误下问。",1,"story_zgl_32","story_zgl_32","刘备",202,},
        [4312] = {7312,6,1,"刘备",202,0,"司马德操之言，徐元直之语，岂虚谈哉？望先生不弃鄙贱，曲赐教诲。","司马德操之言，徐元直之语，岂虚谈哉？望先生不弃鄙贱，曲赐教诲。",1,"story_lb2_226","story_lb2_226","诸葛亮",203,},
