        [4314] = {7312,8,2,"诸葛亮",203,0,"德操、元直，世之高士。亮乃一耕夫耳，安敢谈天下事？二公谬举矣。将军奈何舍美玉而求顽石乎？","德操、元直，世之高士。亮乃一耕夫耳，安敢谈天下事？二公谬举矣。将军奈何舍美玉而求顽石乎？",1,"story_zgl_33","story_zgl_33","刘备",202,},
